"use strict";(self.webpackChunkrhapha_base=self.webpackChunkrhapha_base||[]).push([[9942],{14747:(e,s,n)=>{n.r(s),n.d(s,{assets:()=>a,contentTitle:()=>o,default:()=>u,frontMatter:()=>i,metadata:()=>h,toc:()=>d});var t=n(74848),r=n(28453);const i={title:"March"},o=void 0,h={id:"rhaphsody/2021/march",title:"March",description:"SPEAK WORDS FROM THE FATHER March 1st, 2021",source:"@site/docs/rhaphsody/2021/march.md",sourceDirName:"rhaphsody/2021",slug:"/rhaphsody/2021/march",permalink:"/rhaphsody/2021/march",draft:!1,unlisted:!1,editUrl:"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/docs/rhaphsody/2021/march.md",tags:[],version:"current",frontMatter:{title:"March"},sidebar:"tutorialSidebar",previous:{title:"June",permalink:"/rhaphsody/2021/june"},next:{title:"May",permalink:"/rhaphsody/2021/may"}},a={},d=[{value:"SPEAK WORDS FROM THE FATHER March 1st, 2021",id:"speak-words-from-the-father-march-1st-2021",level:2},{value:"BE VIGILANT IN THE SPIRIT March 2nd, 2021",id:"be-vigilant-in-the-spirit-march-2nd-2021",level:2},{value:"HAVE NO FEAR OF MAN March 3rd, 2021",id:"have-no-fear-of-man-march-3rd-2021",level:2},{value:"KEEP \u201cPUSHING\u201d IN PRAYER March 4th, 2021",id:"keep-pushing-in-prayer-march-4th-2021",level:2},{value:"STAND OUT AND BE COURAGEOUS March 5th, 2021",id:"stand-out-and-be-courageous-march-5th-2021",level:2},{value:"THINK BEYOND YOURSELF March 6th, 2021",id:"think-beyond-yourself-march-6th-2021",level:2},{value:"DIRECT THEIR COURSE THROUGH PRAYER March 7th, 2021",id:"direct-their-course-through-prayer-march-7th-2021",level:2},{value:"CONSCIOUSLY PRACTISE THE WORD March 8th, 2021",id:"consciously-practise-the-word-march-8th-2021",level:2},{value:"STAND IN HIS RIGHTEOUSNESS March 9th, 2021",id:"stand-in-his-righteousness-march-9th-2021",level:2},{value:"BE PREPARED AHEAD OF THE EVIL DAY March 10th, 2021",id:"be-prepared-ahead-of-the-evil-day-march-10th-2021",level:2},{value:"A SOLID FOUNDATION WITH THE WORD March 11th, 2021",id:"a-solid-foundation-with-the-word-march-11th-2021",level:2},{value:"THE STAKES ARE HIGH March 12th, 2021",id:"the-stakes-are-high-march-12th-2021",level:2},{value:"WE\u2019VE BEEN ENTHRONED March 13th, 2021",id:"weve-been-enthroned-march-13th-2021",level:2},{value:"OUR WEAPONS OF WORDS March 14th, 2021",id:"our-weapons-of-words-march-14th-2021",level:2},{value:"VICTORY UNTO VICTORY March 15th, 2021",id:"victory-unto-victory-march-15th-2021",level:2},{value:"GLORY THROUGH THE CHURCH March 16th, 2021",id:"glory-through-the-church-march-16th-2021",level:2},{value:"WORSHIP WITH KNOWLEDGE March 17th, 2021",id:"worship-with-knowledge-march-17th-2021",level:2},{value:"PERSECUTED FOR HIS SAKE March 18th, 2021",id:"persecuted-for-his-sake-march-18th-2021",level:2},{value:"HOLY, FAULTLESS AND GUILTLESS March 19th, 2021",id:"holy-faultless-and-guiltless-march-19th-2021",level:2},{value:"PRAY FOR WORLD LEADERS March 20th, 2021",id:"pray-for-world-leaders-march-20th-2021",level:2},{value:"CHRIST OUR LIGHT March 21st, 2021",id:"christ-our-light-march-21st-2021",level:2},{value:"MARKED FOR VICTORY AND SUCCESS March 22nd, 2021",id:"marked-for-victory-and-success-march-22nd-2021",level:2},{value:"PERFECT COMPLETION March 23rd, 2021",id:"perfect-completion-march-23rd-2021",level:2},{value:"RECEIVING FORGIVENESS IS AN ACT OF FAITH March 24th, 2021",id:"receiving-forgiveness-is-an-act-of-faith-march-24th-2021",level:2},{value:"DOMINATE YOUR WORLD March 25th, 2021",id:"dominate-your-world-march-25th-2021",level:2},{value:"CLOTHED IN GLORY March 26th, 2021",id:"clothed-in-glory-march-26th-2021",level:2},{value:"MORE THAN LETTERS March 27th, 2021",id:"more-than-letters-march-27th-2021",level:2},{value:"SERVE THE LORD HEARTILY March 28th, 2021",id:"serve-the-lord-heartily-march-28th-2021",level:2},{value:"IT\u2019S SOONER THAN YOU THINK March 29th, 2021",id:"its-sooner-than-you-think-march-29th-2021",level:2},{value:"THE SANCTIFIER IN YOU March 30th, 2021",id:"the-sanctifier-in-you-march-30th-2021",level:2},{value:"THE NEW AND LIVING WAY March 31st, 2021",id:"the-new-and-living-way-march-31st-2021",level:2}];function l(e){const s={blockquote:"blockquote",br:"br",em:"em",h2:"h2",hr:"hr",img:"img",p:"p",strong:"strong",...(0,r.R)(),...e.components},{Details:n}=s;return n||function(e,s){throw new Error("Expected "+(s?"component":"object")+" `"+e+"` to be defined: you likely forgot to import, pass, or provide it.")}("Details",!0),(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(s.h2,{id:"speak-words-from-the-father-march-1st-2021",children:"SPEAK WORDS FROM THE FATHER March 1st, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-1-speak-words-from-the-father.jpg",alt:"March 1st, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"For I have not spoken of myself; but the Father which sent me, he gave me a commandment, what I should say, and what I should speak (John 12:49)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Have you ever wondered why some of the words you speak produce the wrong results? Perhaps you\u2019re not speaking words from the Father. Jesus spoke the Father\u2019s words. He knew exactly the right words to say in every situation, to everyone, and concerning everything. The secret is what He told us in our theme verse."}),"\n",(0,t.jsxs)(s.p,{children:["Imagine what transpired in Luke 7:11-17. He was on a journey to the city of Nain, and as He arrived at the gate of the city, He saw a funeral procession of a young man who was the only son of a widow. Moved with compassion, He touched the casket and said, ",(0,t.jsx)(s.em,{children:"\u201c\u2026Young man, I say unto thee, Arise. And he that was dead sat up, and began to speak. And he delivered him to his mother\u201d"})," (Luke 7:14-15). He raised the lifeless body of that young man back to life with words!"]}),"\n",(0,t.jsxs)(s.p,{children:["Over a raging tempest, the Bible says, ",(0,t.jsx)(s.em,{children:"\u201c\u2026he arose, and rebuked the wind, and said unto the sea, Peace, be still. And the wind ceased, and there was a great calm\u201d"})," (Mark 4:39). It made no difference how turbulent the weather was, Jesus hushed them with words. The words He spoke came from the Father. He said in John 12:50, ",(0,t.jsx)(s.em,{children:"\u201c\u2026whatsoever I speak therefore, even as the Father said unto me, so I speak.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"We\u2019re to have His kind of results, because the Father has given us words to speak. Don\u2019t speak words that you learnt from the world; speak the Word of God. Get your language from the Word of God. That\u2019s why you have to keep studying the Scriptures (2 Timothy 2:15). Think and talk like Jesus, and you\u2019ll have His extraordinary results."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"Blessed be God! My words are divine, infused with power to change circumstances. The Father\u2019s words dwell in me richly, and I speak them forth in wisdom. Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Colossians 3:16; John 6:63"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Have you ever wondered why some of the words you speak produce the wrong results? Perhaps you\u2019re not speaking words from the Father. Jesus spoke the Father\u2019s words."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 9:14-32; Numbers 7-8",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 19:1-12; Exodus 10"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"be-vigilant-in-the-spirit-march-2nd-2021",children:"BE VIGILANT IN THE SPIRIT March 2nd, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-2-be-vigilant-in-the-spirit.jpg",alt:"March 2nd, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"They which builded on the wall, and they that bare burdens, with those that laded, every one with one of his hands wrought in the work, and with the other hand held a weapon (Nehemiah 4:17)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Nehemiah was faced with an intense opposition when he decided to build the wall of Jerusalem. The Jews\u2019 enemies were determined to stop the building by slaying the builders. But Nehemiah wasn\u2019t ignorant of their evil plans and intentions."}),"\n",(0,t.jsx)(s.p,{children:"As he led God\u2019s people to build the wall, they prepared to ward off any form of attack. Each one held a weapon with the other hand, taking every necessary precaution against any onslaught from the enemy. This was written for our learning."}),"\n",(0,t.jsx)(s.p,{children:"We\u2019re partners and co-labourers with the Lord in building His Church\u2014His body\u2014winning souls and carrying out the work of the ministry. While at it, we must be ready to fight against the decrees of darkness to force us to live in a world, an atmosphere or governmental system that\u2019s hostile to the Gospel."}),"\n",(0,t.jsxs)(s.p,{children:["Satan, knowing that his time is short, is trying to move ahead to create the environment of the anti-Christ before his time. So, we must be on the alert and on the offensive. 1 Peter 5:8 says, \u201c",(0,t.jsx)(s.em,{children:"Be sober, be vigilant\u2026.\u201d"})," Be vigilant in the spirit, listening in, and with, your spirit as you speak in tongues and meditate on the Scriptures. The Lord will give you strategies and guidance on how to effectively dismantle the structures of the adversary."]}),"\n",(0,t.jsx)(s.p,{children:"Refuse to be moved by some of the happenings in the world today\u2014the confusion, fear and deception. By the power of God\u2019s Spirit, we (the Church) are exercising our authority in Christ, bringing in the new order of things. Get vitally involved in this. Pray like you\u2019ve never done! When we call for days of global prayer and fasting, be a part of it. We\u2019re setting in place a new world order in the Spirit, where the Gospel has free course, and in preparation for our soon coming Lord. Amen."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019m strong in the Lord, and in the power of His might. He opens my eyes to spiritual realities and unveils His thoughts to my spirit. And today, I take charge of situations from the realm of the Spirit. I pray against any attempt of the devil to force the Church of Jesus Christ to live in a world of the beast before his time. Until the rapture of the Church, the Name of Jesus Christ shall be glorified in all nations. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 Thessalonians 5:17; 2 Corinthians 10:3; Ephesians 6:18"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Be vigilant in the spirit, listening in, and with, your spirit as you speak in tongues and meditate on the Scriptures. The Lord will give you strategies and guidance on how to effectively dismantle the structures of the adversary."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 9:33-50; Numbers 9-10",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 19:13-22; Exodus 11"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"have-no-fear-of-man-march-3rd-2021",children:"HAVE NO FEAR OF MAN March 3rd, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-3-have-no-fear-of-man.jpg",alt:"March 3rd, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"\u2026Be not ye afraid of them: remember the Lord, which is great and terrible, and fight for your brethren, your sons, and your daughters, your wives, and your houses (Nehemiah 4:14)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Many years ago, while I was in the university, the government of the day, a military government, banned all religious activities on campus. Some of the members of our fellowship came to convince me to comply, but I had heard from God; He told me to remain on campus."}),"\n",(0,t.jsx)(s.p,{children:"Some of them weren\u2019t ready to stand with me, perhaps, because they were afraid. But if God had told me to continue, why should I stop because of what someone said? Always, God\u2019s Word or counsel to you is what counts. He made the Vice-Chancellor, Governor, President, Prime Minister, etc."}),"\n",(0,t.jsx)(s.p,{children:"The Bible says the powers that be are ordained of Him; He put them in office. If they go rogue against Him by coming up with laws and policies that don\u2019t favour His righteous cause, you\u2019re not obliged to comply. No authority supersedes God\u2019s authority. So, I went ahead with the programme I had scheduled on campus, because I\u2019d rather obey God; and it was a remarkable success."}),"\n",(0,t.jsxs)(s.p,{children:["Subsequently, we were never stopped from holding services on campus till I graduated. Was I defying the government? No! I was only obeying the orders of a higher government. It was the same with the apostles. Peter and the other apostles confronted the authorities in their day. Once, they said, ",(0,t.jsx)(s.em,{children:"\u201c...You yourselves judge which is right in God\u2019s sight---to obey you or to obey God\u201d"})," (Acts 4:19 GNB). As a man or woman of God, as a Christian, as one who walks with God, have no fear of men! Have no fear of their laws or decrees; have no fear of anything. The Bible says there\u2019s no wisdom against the Lord (Proverbs 21:30)."]}),"\n",(0,t.jsxs)(s.p,{children:["Human beings can never come together and come up with enough wisdom or strategy against God. It\u2019s impossible. When you\u2019re walking in sync with His plan and purpose for your life, it makes no difference what anyone thinks or does; it doesn\u2019t matter the threats; you\u2019ll win. The opposition may come from your family, your city, country, or government; have no fear, ",(0,t.jsx)(s.em,{children:"\u201c...because greater is he that is in you, than he that is in the world\u201d"})," (1 John 4:4)."]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019m confident in the power of God\u2019s Word to put me over always, irrespective of the circumstances or opposition. I stand in faith against any law, decree, government or individuals who have set themselves against the Church of Jesus Christ, and I declare that their defence is gone from them! Glory to God!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Psalm 27:1-3; Acts 5:25-29; 1 John 4:4"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"As a man or woman of God, as a Christian, as one who walks with God, have no fear of men! Have no fear of their laws or decrees; have no fear of anything."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 10:1-31; Numbers 11-13",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 19:23-30; Exodus 12"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"keep-pushing-in-prayer-march-4th-2021",children:"KEEP \u201cPUSHING\u201d IN PRAYER March 4th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-4-keep-pushing-in-prayer.jpg",alt:"March 4th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Confess your faults one to another, and pray one for another, that ye may be healed. The effectual fervent prayer of a righteous man availeth much (James 5:16)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["When the Bible says there was no rain in the land of Israel according to the word of Elijah, it wasn\u2019t that Elijah suddenly woke up and said, \u201cThere\u2019ll be no rain in Israel for the next three and half years.\u201d No, the Bible says he prayed earnestly. 1 Kings 18:42 shows how he prayed; it says Elijah ",(0,t.jsx)(s.em,{children:"\u201cbowed deeply in prayer, his face between his knees\u201d"})," (MSG)."]}),"\n",(0,t.jsx)(s.p,{children:"The Amplified Classic version of our theme verse aptly describes this kind of prayer*: \u201c...The earnest (heartfelt, continued) prayer of a righteous man makes tremendous power available [dynamic in its working].\u201d* It says the prayer is earnest, and there\u2019s a persistence about it. In other words, it\u2019s a sustained kind of prayer that\u2019s marked by a deep feeling of conviction."}),"\n",(0,t.jsx)(s.p,{children:"This isn\u2019t the kind of prayer you pray at the dining table or when you\u2019re hurrying off to work. There\u2019re certain times and situations where you don\u2019t just speak the word and go away; you persist in prayer. You keep pushing. You may be saying the same thing, but you\u2019re pushing."}),"\n",(0,t.jsx)(s.p,{children:"Jesus did that too in the Garden of Gethsemane. He said the same things in prayer, and He prayed that way for an hour. It wasn\u2019t a two-minute prayer. He persisted, because he understood the stakes of the moment. We\u2019re in a similar moment today. The stakes are high. You\u2019ve got to take your personal prayer life more seriously."}),"\n",(0,t.jsx)(s.p,{children:"Always stir yourself and respond to the call and urgency of prayer from your spirit, declaring the Word and crushing the forces of darkness, in the spirit. It may look like nothing is changing, but you keep pushing. You might ask, \u201cBut when do I stop?\u201d You stop when you \u201cknow that you know\u201d in your spirit that it\u2019s done! Glory to God! You\u2019ll surely get the note of victory."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Blessed Father, thank you for the privilege and opportunity of prayer by which I exercise my authority in Christ, to activate and walk in the consciousness of the divine presence, and enforce your will in the earth. I\u2019m an effective intercessor through the Word of faith and earnest, heartfelt, continued prayer. I declare that the nations and leaders of the world are under the divine influence of your mighty power, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Colossians 4:2; 1 Thessalonians 5:17; Ephesians 6:18"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"There\u2019re certain times and situations where you don\u2019t just speak the word and go away; you persist in prayer. You keep pushing. The stakes are high. You\u2019ve got to take your personal prayer life more seriously."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 10:32-52; Numbers 14-15",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 20:1-16; Exodus 13"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"stand-out-and-be-courageous-march-5th-2021",children:"STAND OUT AND BE COURAGEOUS March 5th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-5-stand-out-and-be-courageous.jpg",alt:"March 5th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"There shall not any man be able to stand before thee all the days of thy life: as I was with Moses, so I will be with thee: I will not fail thee, nor forsake thee (Joshua 1:5)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"I often recall how, many years ago, cult activities in tertiary institutions were on the rise. Some Christian groups were threatened and intimidated from time to time by these nocturnal groups. But as I studied my Bible and read about the exploits of people like David, Moses, Jephthah, etc., I couldn\u2019t understand why any Christian would be intimidated by cultists. So, I prayed, \u201cLord, give me just one chance; I\u2019ll force those cult members out!\u201d"}),"\n",(0,t.jsx)(s.p,{children:"Finally, I got into the university, and then came my first experience in my first year! One night, the cult members came dancing into the campus in their full regalia, and there was complete pandemonium. But while many scampered in different directions, looking for where to hide, I got on my knees, spoke in tongues, and I said, \u201cLord Jesus, by your grace, I\u2018ll deal with this; they\u2019ll never have such effrontery on this campus again.\u201d"}),"\n",(0,t.jsx)(s.p,{children:"Then on another occasion, a year later, they sent out a message that they were \u201csailing\u201d that night. And I said, \u201cSailing where? Never!\u201d When it was night, I gathered some of the brethren, and said, \u201cWe\u2019re sailing tonight!\u201d As they (the cultists) were making noise and rabble-rousing, we were out on the other side, dancing and singing, \u201cThe weapons of our warfare are not carnal, but they are mighty through the Holy Ghost!\u201d\xa0"}),"\n",(0,t.jsx)(s.p,{children:"We were singing with the consciousness that in the spirit, we\u2019re declaring them arrested. Guess what? The next day, the Police came looking for them, and they were arrested! Praise God! The months following, several of the cult leaders and members received salvation and joined us."}),"\n",(0,t.jsx)(s.p,{children:"As Christians, we should never be intimidated by anything or anyone. It\u2019s something the Lord said to me years ago; He said, \u201cAs I was with Moses, so I\u2019ll be with you. I\u2019ll not fail you nor forsake you\u201d; and He\u2019s saying the same to you, even now. Be strong, and very courageous. God\u2019s Word is dependable. That\u2019s what I\u2019m telling you today. You can stake your life on it. When others are terrorized and full of fear concerning any situation, stand out, and be full of courage."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"The Lord is my light and my salvation; whom shall I fear? The Lord is the strength of my life; of whom shall I be afraid? Though a host should encamp against me, my heart shall not fear, for greater is He that\u2019s in me, than he that\u2019s in the world. Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 John 4:4; 1 Corinthians 16:13; Ephesians 6:13"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"As Christians, we should never be intimidated by anything or anyone. God\u2019s Word is dependable. When others are terrorized and full of fear concerning any situation, stand out, and be full of courage."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 11:1-26; Numbers 16-17",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 20:17-23; Exodus 14"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"think-beyond-yourself-march-6th-2021",children:"THINK BEYOND YOURSELF March 6th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-6-think-beyond-yourself.jpg",alt:"March 6th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Don\u2019t push your way to the front; don\u2019t sweet-talk your way to the top. Put yourself aside, and help others get ahead. Don\u2019t be obsessed with getting your own advantage. Forget yourselves long enough to lend a helping hand (Philippians 2:3-4 MSG)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"1 Timothy 2:1-4 says*, \u201cI exhort therefore, that, first of all, supplications, prayers, intercessions, and giving of thanks, be made for all men; For kings, and for all that are in authority; that we may lead a quiet and peaceable life in all godliness and honesty. For this is good and acceptable in the sight of God our Saviour; Who will have all men to be saved, and to come unto the knowledge of the truth.\u201d* It\u2019s our responsibility to change the world through prayer, ministry and protagonism. That\u2019s what you have to do. Otherwise, you haven\u2019t lived."}),"\n",(0,t.jsxs)(s.p,{children:["In Nehemiah 4:14, as the children of Israel began to build the wall of Jerusalem and prepared to defend it, Nehemiah said to the people, ",(0,t.jsx)(s.em,{children:"\u201c\u2026fight for your brethren, your sons, and your daughters, your wives, and your houses.\u201d"})," He didn\u2019t say, \u201cFight for yourselves.\u201d And the word \u201chouses\u201d there doesn\u2019t mean buildings; he was referring to clans, whole families, like you\u2019d say, \u201cThe house of Joseph.\u201d He was calling out the champion spirit in them."]}),"\n",(0,t.jsxs)(s.p,{children:["Champions don\u2019t fight for themselves; they fight for others. That\u2019s what we read in our theme verse; it says, ",(0,t.jsx)(s.em,{children:"\u201cPut yourself aside, and help others get ahead. Don\u2019t be obsessed with getting your own advantage.\u201d"})," When you pray, it shouldn\u2019t always be about your business, your family or things related to you; it should be for a greater cause. Think beyond yourself."]}),"\n",(0,t.jsx)(s.p,{children:"There\u2019re many who are going through a lot of hurt for whom we must pray. The Bible says of the Lord Jesus that He\u2019s touched with the feeling of our infirmities (Hebrews 4:15). Be moved by the conditions of the sinners and others who are suffering around you. Let the plight of the poor, the sick, and the downtrodden stir compassion in you to intercede for them and cause a change in their lives today. We\u2019re to continue in intercession for the nations, leaders, and peoples of the world, especially in these last days."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Thank you blessed Father, for the opportunity to stand in the gap for others, to cause a change in their lives and circumstances. I pray for those destined to receive salvation today all around the world, that the light of the glorious Gospel will shine in their hearts, dispelling the darkness, translating them into the liberty of the sons of God, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 Timothy 2:1-4; Ezekiel 33:7"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Champions don\u2019t fight for themselves; they fight for others. When you pray, it shouldn\u2019t always be about your business, your family or things related to you; it should be for a greater cause. Think beyond yourself."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 11:27-33; Mark 12:1-17; Numbers 18-19",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 20:24-34; Exodus 15"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"direct-their-course-through-prayer-march-7th-2021",children:"DIRECT THEIR COURSE THROUGH PRAYER March 7th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-7-direct-their-course-through-prayer.jpg",alt:"March 7th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"I exhort therefore, that, first of all, supplications, prayers, intercessions, and giving of thanks, be made for all men; For kings, and for all that are in authority; that we may lead a quiet and peaceable life in all godliness and honesty (1 Timothy 2:1-2)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Our theme scripture is the Spirit\u2019s admonition through Paul to the Church on the importance of earnest intercession for all men, our countries of domicile, and the leaders therein. The result of such is an overwhelming experience of God\u2019s blessings of peace and prosperity. This is part of what you do in obedience\u2014praying, contending for the hearts of men in your country, and around the world\u2014that God\u2019s righteousness be established, and all forms of evil, turmoil, wickedness and terror, be thwarted."}),"\n",(0,t.jsx)(s.p,{children:"Today, the happenings in our world are a great source of concern. But the Church holds the solution. The Church is the only restraining power against evil in the earth today (2 Thessalonians 2:7). So, you must pray and not complain. If you don\u2019t pray for your country, the leaders and people, where would peace come from?"}),"\n",(0,t.jsx)(s.p,{children:"Read our theme verse again; God\u2019s directive on how we must pray in order to experience peace in our nations is clear; it\u2019s not by bearing arms or becoming riotous."}),"\n",(0,t.jsx)(s.p,{children:"Rise to the challenge and take up the responsibility of prayer. Pray earnestly and fervidly in the Spirit for the nations and leaders of the countries where you live or work, and the rest of the world. Declare that God\u2019s righteousness prevails, and His glory rests on, and influences men\u2019s hearts as God\u2019s people boldly proclaim the Gospel."}),"\n",(0,t.jsx)(s.p,{children:"Pray also for ministers, that they\u2019d be granted more grace to preach the Gospel with boldness and power, that many more souls would be added to the Church all around the world. Until the rapture takes place, we hold the true reins of power in the nations, and have the authority to direct their course through prayer."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear heavenly Father, I pray that your peace, progress and prosperity will reign in the nations of the world and that you\u2019ll grant the ministers of the Gospel the courage to preach and minister your Word boldly. I pray that the power and influence of their message will kindle your love in people\u2019s hearts. Thank you for the tremendous and glorious transformation of many lives through the Gospel today, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Colossians 4:2; Luke 18:1; Jeremiah 29:7"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"If you don\u2019t pray for your country, the leaders and people, where would peace come from? Rise to the challenge and take up the responsibility of prayer. Pray for the nations and leaders of the world."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 12:18-44; Numbers 20-21",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 21:1-11; Exodus 16"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"consciously-practise-the-word-march-8th-2021",children:"CONSCIOUSLY PRACTISE THE WORD March 8th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-8-consciously-practise-the-word.jpg",alt:"March 8th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But whoso looketh into the perfect law of liberty, and continueth therein, he being not a forgetful hearer, but a doer of the work, this man shall be blessed in his deed (James 1:25)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Consciously practising the Word is how you can enjoy its full blessings in your life, because the power of God is in His Word. For example, the Word says*, \u201c...Love your enemies, bless them that curse you, do good to them that hate you, and pray for them which despitefully use you, and persecute you\u201d* (Matthew 5:44). Consciously practise this; let it be your daily walk."}),"\n",(0,t.jsxs)(s.p,{children:["When you start out practising something, you may miss it, but don\u2019t give up and say, \u201cWell, I tried.\u201d Keep at it until you gain mastery. Again, the Word says, ",(0,t.jsx)(s.em,{children:"\u201c...take no thought for your life...\u201d"})," (Luke 12:22); in other words, have no cares. Practise it. Don\u2019t find yourself worrying over the mundane things of life, with fear of lack; rather, by prayer and supplication with thanksgiving, let your requests be made known to God."]}),"\n",(0,t.jsx)(s.p,{children:"Psalm 1:3 reveals something phenomenal about the extraordinary life of blessings you have in Christ. It says you\u2019re like a tree planted by the rivers of water\u2014ever luscious and green\u2014fruitful and eternally productive. Affirm this every day about yourself. Irrespective, and independent of the happenings in the world, be unmoved, because you know you\u2019re empowered to prosper. For you, it\u2019s blessings only. Hallelujah!"}),"\n",(0,t.jsxs)(s.p,{children:["We\u2019re Word-practitioners; Jesus Christ is the Living Word, and we\u2019re born after Him. Paul said, \u201cYe are the Epistles of Christ\u201d (2 Corinthians 3:3). That means we\u2019re the expression of the Word. The Apostle Peter referenced a similar thought in 1 Peter 1:23, saying, ",(0,t.jsx)(s.em,{children:"\u201cBeing born again, not of corruptible seed, but of incorruptible, by the word of God, which liveth and abideth forever.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"Being born of the Word means you must live in, by and through the Word; you must live it out in your daily walk."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"Righteous Father, how I love your Word; your truth is the light by which I see, and the foundation on which my strength is anchored. I\u2019m blossoming in righteousness, flourishing in glory, producing lasting results and making an undeniable impact with the Gospel, to the praise of your Name. Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"James 1:22-25; Colossians 3:16"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Being born of the Word means you must live in, by and through the Word; you must live it out in your daily walk. Consciously practising the Word is how you can enjoy its full blessings in your life."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 13:1-37; Numbers 22-23",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 21:12-17; Exodus 17"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"stand-in-his-righteousness-march-9th-2021",children:"STAND IN HIS RIGHTEOUSNESS March 9th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-9-stand-in-his-righteousness.jpg",alt:"March 9th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"To declare, I say, at this time his righteousness: that he might be just, and the justifier of him which believeth in Jesus (Romans 3:26)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Jesus Christ has become your righteousness. That\u2019s why in Christianity, we don\u2019t walk in our righteousness; rather, we stand in His righteousness. We declare and boldly affirm that Christ is our righteousness and walk in the light of His righteousness. It\u2019s like being covered in His glory; you\u2019re not seen. It\u2019s His light that\u2019s seen; it\u2019s His glory that\u2019s seen."}),"\n",(0,t.jsxs)(s.p,{children:["It\u2019s His power that\u2019s made manifest as you walk in His righteousness. That\u2019s our blessing as new creatures in Christ Jesus. Don\u2019t be \u201cperformance-conscious\u201d before God. Don\u2019t allow sin and the guilt of your mistakes dissipate your zeal for God and faith in Him. 2 Corinthians 5:21 says, ",(0,t.jsx)(s.em,{children:"\u201cFor he hath made him to be sin for us, who knew no sin; that we might be made the righteousness of God in him.\u201d"})]}),"\n",(0,t.jsxs)(s.p,{children:["You\u2019ve become the effulgence of His righteousness, because He manifests His righteousness through you. He took your place of sin, and gave you His righteousness. Now, He manifests His righteousness through you. This is an amazing reality! Your new life started with the resurrection of Jesus Christ: ",(0,t.jsx)(s.em,{children:"\u201cTherefore we are buried with him by baptism into death: that like as Christ was raised up from the dead by the glory of the Father, even so we also should walk in newness of life\u201d"})," (Romans 6:4)."]}),"\n",(0,t.jsxs)(s.p,{children:["2 Corinthians 5:17 says, ",(0,t.jsx)(s.em,{children:"\u201cTherefore if any man be in Christ, he is a new creature\u2026\u201d"}),"; a new species or kind of man, born in righteousness. This is the reason you can practise righteousness and produce fruits and works of righteousness, because that\u2019s your nature in Christ Jesus. Hallelujah!"]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"My righteousness is the righteousness of Jesus Christ; therefore, I have boldness in the presence of God, to worship Him in love and holiness of truth. The blood of Jesus gave me access and life, and by His resurrection, I stand justified forever, with dominion over Satan, circumstances and the cohorts of darkness. Blessed be God!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Romans 6:4-5; 1 Corinthians 1:30; Philippians 3:8-9"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Jesus Christ has become your righteousness. That\u2019s why in Christianity, we don\u2019t walk in our righteousness; rather, we stand in His righteousness. Christ is our righteousness and walk in the light of His righteousness."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 14:1-26; Numbers 24-26",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 21:18-32; Exodus 1"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"be-prepared-ahead-of-the-evil-day-march-10th-2021",children:"BE PREPARED AHEAD OF THE EVIL DAY March 10th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-10-be-prepared-ahead-of-the-evil-day.jpg",alt:"March 10th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Finally, my brethren, be strong in the Lord, and in the power of his might\u2026 For we wrestle not against flesh and blood, but against principalities, against powers, against the rulers of the darkness of this world, against spiritual wickedness in high places. Wherefore take unto you the whole armour of God, that ye may be able to withstand in the evil day, and having done all, to stand (Ephesians 6:10-13)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["The first part of our opening scripture admonishes that you be strong in the Lord, and in the power of His might. The Lord never tells us to do something we couldn\u2019t do. If He says, ",(0,t.jsx)(s.em,{children:"\u201cBe strong in the Lord,\u201d"})," it means that\u2019s what you need to do. He gives further instructions in the eleventh verse: ",(0,t.jsx)(s.em,{children:"\u201cPut on the whole armour of God that ye may be able to stand against the wiles of the devil.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"This is telling you to prepare, and stay prepared ahead of the evil day. He tells you what to do against the devil. If you put on the whole armour of God as He has said, you\u2019ll be able to stand against all the stratagems, schemes, maneuvers, and manipulations of the devil."}),"\n",(0,t.jsx)(s.p,{children:"The reason many falter when they face challenges is that they don\u2019t act on the Word. In the verses we read in our theme scripture, God instructed us clearly on what to do. What He said in verse thirteen is the reason for verses 10-12. There\u2019s a day of trouble (the evil day); it\u2019s to be anticipated. There\u2019s an enemy out there seeking whom to devour, and when he has the opportunity, he\u2019ll strike. But we\u2019re not ignorant of His devices."}),"\n",(0,t.jsx)(s.p,{children:"Satan isn\u2019t a factor. As long as you\u2019re strong in the Lord\u2014keeping His Word in your heart and in your mouth\u2014you\u2019ll discomfit the enemy whenever he shows up. As long as the Word of God is your arsenal, you\u2019ll always win. The Word of God is the sword of the Spirit with which you cut the devil down. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"In the Name of Jesus Christ, I exercise dominion over Satan and his cohorts, and I cut down all his machinations, schemes, deceptions and devices against my nation and my city. With the Word of God in my mouth, I triumph gloriously, reigning and prevailing over circumstances. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 John 4:4; James 4:7; Ephesians 6:10-18"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"The Lord never tells us to do something we couldn\u2019t do. He tells you what to do against the devil. Put on the whole armour of God as He has said."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 14:27-52; Numbers 27-28",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 21:33-46; Exodus 19"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"a-solid-foundation-with-the-word-march-11th-2021",children:"A SOLID FOUNDATION WITH THE WORD March 11th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-11-a-solid-foundation-with-the-word.jpg",alt:"March 11th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But we will give ourselves continually to prayer, and to the ministry of the word (Acts 6:4)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"A solid foundation of the Word of God is what will determine your reaction and disposition to life and circumstances around you. Think about the happenings in the world in the past months; the reaction and actions of some Christians is proof they never had the right foundation. Some were completely disillusioned and ran into hiding\u2014to \u201cisolate\u201d themselves\u2014while looking to government leaders for help and guidance. They were terrified by the same things that frightened the world; that shouldn\u2019t be."}),"\n",(0,t.jsxs)(s.p,{children:["The Church isn\u2019t a group of sissies; we\u2019re soldiers. We\u2019re tough for Christ. How could a Christian be afraid of a virus, after Jesus said in Luke 10:19, ",(0,t.jsx)(s.em,{children:"\u201cBehold, I give unto you power to tread on serpents and scorpions, and over all the power of the enemy: and nothing shall by any means hurt you.\u201d"})," This is a sovereign declaration; it\u2019s unequivocal. You\u2019re superior to anything that hurts or binds; you\u2019re \u201cuninfectable.\u201d"]}),"\n",(0,t.jsxs)(s.p,{children:["But you see, if you don\u2019t know this truth, you\u2019d be terrified. This is why we bring you the Word of God every day, to build you up in Christ, to impart knowledge to you. Acts 20:32 says, ",(0,t.jsx)(s.em,{children:"\u201cAnd now, brethren, I commend you to God, and to the word of his grace, which is able to build you up\u2026.\u201d"})," If you\u2019re brought up on the foundation of the Word of God, nothing will move you."]}),"\n",(0,t.jsxs)(s.p,{children:["Recall the words of Jesus in Luke 6:47-49 as He distinguished between the one whose life is founded on the Word of God, and the one whose life isn\u2019t. He said, ",(0,t.jsx)(s.em,{children:"\u201cWhosoever cometh to me, and heareth my sayings, and doeth them, I will shew you to whom he is like: He is like a man which built an house, and digged deep, and laid the foundation on a rock: and when the flood arose, the stream beat vehemently upon that house, and could not shake it: for it was founded upon a rock. But he that heareth, and doeth not, is like a man that without a foundation built an house upon the earth; against which the stream did beat vehemently, and immediately it fell; and the ruin of that house was great.\u201d"})," Build a solid foundation for your life with the Word of God."]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I consciously set my thinking on spiritual things, building a deep and firm foundation through consistent study and meditation on the Word of God. I\u2019m tough for the Lord, a gallant soldier in His end-time army! As I give myself to prayer and to the ministry of the Word, the will and purposes of God are established in the earth and in the hearts of men, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Matthew 7:24-27; Romans 12:11-12; Colossians 3:1-3"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"A solid foundation of the Word of God is what will determine your reaction and disposition to life and circumstances around you. If you\u2019re brought up on the foundation of the Word of God, nothing will move you."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 14:53-72; Numbers 29-31",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 22:1-14; Exodus 20"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"the-stakes-are-high-march-12th-2021",children:"THE STAKES ARE HIGH March 12th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-12-the-stakes-are-high.jpg",alt:"March 12th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Not slothful in business; fervent in spirit; serving the Lord (Romans 12:11)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Clearly, we see from the Scriptures that we\u2019re in the final days before the rapture of the Church. This isn\u2019t the time to loaf around; there\u2019s a lot the Lord wants to do through you with the short time left. Our theme verse says*, \u201cNot slothful in business; fervent in spirit; serving the Lord.\u201d* You\u2019ve got to work with the mentality that His appearing could be any minute, and become very passionate in your spirit for the Gospel\u2014ensuring its spread and impact around the world."}),"\n",(0,t.jsxs)(s.p,{children:["As the Scriptures tell us, the Antichrist is only going to be given three and a half years for the Great Tribulation. It\u2019s going to be a period of intense trouble, worse than the world has ever experienced and will ever experience (Matthew 24:21); and it\u2019ll be world-wide in scope. Jesus said, ",(0,t.jsx)(s.em,{children:"\u201cAnd except those days should be shortened, there should no flesh be saved\u2026\u201d"})," (Matthew 24:22)."]}),"\n",(0,t.jsx)(s.p,{children:"Imagine the kind of evil and destruction that\u2019ll be perpetrated by the Antichrist for only three and a half years that warrants a divine shortening. That also tells us something: We can do a lot of good within a short time; we can shake up this world. It begins with you giving more attention to prayer, especially praying for the impact of the Gospel around the world in these last days."}),"\n",(0,t.jsx)(s.p,{children:"This is very important; the stakes are very high. It\u2019s not just about you; it\u2019s about the future of our world. Before the Rapture, we\u2019ve got to dominate this world like never before! Jesus isn\u2019t planning to \u201csnatch\u201d us out of this world as though we\u2019ve been victims. Remember that He overcame the world. He\u2019s coming for a victorious Church; a triumphant Church. Glory to God!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"The Gospel of Christ burns in my heart, like fire shut up in my bones! Thank you, dear Father, for the honour of being a custodian of this life-changing message, the message of power by which you save, heal, protect, deliver, preserve and prosper. I\u2019ve been endowed with the ability and grace to lead many to Christ, unveiling to them the mysteries of the Kingdom, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Romans 12:11; Daniel 12:3; 2 Timothy 3:1"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Clearly, we see from the Scriptures that we\u2019re in the final days before the rapture of the Church. This isn\u2019t the time to loaf around; there\u2019s a lot the Lord wants to do through you with the short time left."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 15:1-20; Numbers 32-33",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 22:15-22; Exodus 21"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"weve-been-enthroned-march-13th-2021",children:"WE\u2019VE BEEN ENTHRONED March 13th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-13-weve-been-enthroned.jpg",alt:"March 13th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But God, who is rich in mercy, for his great love wherewith he loved us\u2026And hath raised us up together, and made us sit together in heavenly places in Christ Jesus (Ephesians 2:4-6)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["The phrase \u201csit together\u201d in the scripture above is \u201csugkathizo\u201d (Greek), and it means to give (or take) a seat in company with; a joint-sitting. Think about that! What could be greater than having a joint-sitting with Christ! Weymouth New Testament version renders it so beautifully; it says God ",(0,t.jsx)(s.em,{children:"\u201craised us with Him from the dead, and enthroned us with Him in the heavenly realms as being in Christ Jesus.\u201d"})," We\u2019ve been enthroned! Blessed be God!"]}),"\n",(0,t.jsxs)(s.p,{children:["Now that we\u2019ve been enthroned, what are we supposed to do? Romans 5:17 tells us: ",(0,t.jsx)(s.em,{children:"\u201cFor if by one man\u2019s offence death reigned by one; much more they which receive abundance of grace and of the gift of righteousness shall reign in life by one, Jesus Christ.\u201d"})," The word \u201creign\u201d is \u201cbasileuo\u201d (Greek) which means \u201cto king.\u201d And Revelation 5:10 perfectly corroborates this truth; it says, ",(0,t.jsx)(s.em,{children:"\u201cAnd hast made us unto our God kings and priests: and we shall reign on the earth.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"Yes, at the millennial period, we\u2019re going to be reigning with Christ, according to the Bible. But today in the earth, we\u2019re reigning through Him or by Him, because we\u2019re using His Name; operating in His stead. We\u2019re called to exercise dominion and to live as masters and victors in life through Jesus Christ. We reign over Satan, darkness, the world and all creation."}),"\n",(0,t.jsxs)(s.p,{children:["The Bible says, ",(0,t.jsx)(s.em,{children:"\u201cThe light shines in the darkness, and the darkness has not overcome it\u201d"})," (John 1:5 ESV). Hallelujah! This is why the Bible says, ",(0,t.jsx)(s.em,{children:"\u201cFor the mystery of lawlessness (that hidden principle of rebellion against constituted authority) is already at work in the world, [but it is] restrained only until he who restrains is taken out of the way\u201d"})," (2 Thessalonians 2:7 AMPC). We\u2014the Church\u2014are the ones restraining evil in the world. Jesus said, ",(0,t.jsx)(s.em,{children:"\u201cAs long as I am in the world, I am the light of the world\u201d"})," (John 9:5). In the same way, until we\u2019re taken out of here by the Rapture, darkness can\u2019t prevail, because we have a joint-sitting with Christ in the place of power and dominion. And we exercise that power and dominion now!"]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I have a joint-sitting with Christ in the place of glory and power. As long as I\u2019m in the world, I\u2019m the light of the world! I exercise dominion over circumstances and the spirits of darkness, and I bring them under subjection to the Name of Jesus! Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Ephesians 1:19-22; Colossians 2:10"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"What could be greater than having a joint-sitting with Christ! We\u2019ve been enthroned! Now that we\u2019ve been enthroned, what are we supposed to do? We reign over Satan, darkness, the world and all creation."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 15:21-47; Numbers 34-36",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 22:23-33; Exodus 22"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"our-weapons-of-words-march-14th-2021",children:"OUR WEAPONS OF WORDS March 14th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-14-our-weapons-of-words.jpg",alt:"March 14th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Giving thanks unto the Father\u2026Who hath delivered us from the power of darkness, and hath translated us into the kingdom of his dear Son (Colossians 1:12-13)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"From the scripture above, we see that there\u2019re only two groups of people: those under the power of darkness and those in the Kingdom of God\u2019s dear Son. Until you\u2019re born again and transferred into the Kingdom of God\u2019s dear Son, you remain under the power of darkness, and Satan becomes the god of all that concerns you."}),"\n",(0,t.jsxs)(s.p,{children:["Remember, Jesus said to the Jews who came to arrest Him, ",(0,t.jsx)(s.em,{children:"\u201cYe are of your father the devil, and the lusts of your father ye will do...\u201d"})," (John 8:44). This is why we oftentimes find leaders and rulers of nations that are influenced and controlled by the principalities of darkness. When such leaders make wicked decisions and enforce repressive policies, it\u2019s because they\u2019re being influenced by evil forces. That\u2019s why we pray for leaders, so that instead of being influenced by the forces of darkness, they\u2019d be influenced by the Spirit of God."]}),"\n",(0,t.jsxs)(s.p,{children:["The Bible says, ",(0,t.jsx)(s.em,{children:"\u201cI exhort therefore, that, first of all, supplications, prayers, intercessions, and giving of thanks, be made for all men; For kings, and for all that are in authority\u2026\u201d"})," (1 Timothy 2:1-2). Then, 2 Corinthians 10:4 says, ",(0,t.jsx)(s.em,{children:"\u201cFor the weapons of our warfare are not carnal, but mighty through God to the pulling down of strong holds.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"The word \u201cwarfare\u201d is \u201cstrateia\u201d (Greek) and it\u2019s telling us that we\u2019re in the strategizing office, leading a military expedition in the Spirit. When we speak words in prayer, angels of God and the Spirit of God take those words and weaponize them. That\u2019s what makes them mighty to the pulling down of fortresses."}),"\n",(0,t.jsx)(s.p,{children:"So, as a Christian, recognize the enormity of the responsibility on you, to intercede for leaders of nations. Every so often, pray fervently in the spirit for the leaders in your country. With authoritative words, rebuke the forces of darkness to desist from influencing them, and affirm that their hearts are turned to the Lord to do His will. This is what we must do for leaders of nations to be influenced by God\u2019s wisdom and righteousness; we must pray for them in line with God\u2019s Word."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Lord, I thank you for endowing us with your might to pull down fortresses, ideas planted in the minds of men, and theories they\u2019ve accepted in life that have kept them from moving in the direction of your dream and destiny for them. I declare that Satan\u2019s influence on leaders and rulers of nations of the world is broken! Your light shines brightly in their hearts today, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Ephesians 6:16-18; 2 Corinthians 10:3-5"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"When we speak words in prayer, angels of God and the Spirit of God take those words and weaponize them. That\u2019s what makes them mighty to the pulling down of fortresses"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Mark 16:1-20; Deuteronomy 1-2",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 22:34-46; Exodus 23"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"victory-unto-victory-march-15th-2021",children:"VICTORY UNTO VICTORY March 15th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-15-victory-unto-victory.jpg",alt:"March 15th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But thanks be to God, who always leads us in triumphal procession in Christ and through us spreads everywhere the fragrance of the knowledge of him (2 Corinthians 2:14 NIV)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["You can win every day, and achieve extraordinary success. The Amplified Classic version of the scripture above says, ",(0,t.jsx)(s.em,{children:"\u201cBut thanks be to God, Who in Christ always leads us in triumph [as trophies of Christ\u2019s victory] and through us spreads and makes evident the fragrance of the knowledge of God everywhere.\u201d"})," As we get ourselves prepared in these days before the Rapture, you\u2019re not only going to be triumphant in your city but everywhere you go."]}),"\n",(0,t.jsxs)(s.p,{children:["1 John 5:4 says, ",(0,t.jsx)(s.em,{children:"\u201cFor whatever is born of God is victorious over the world; and this is the victory that conquers the world, even our faith\u201d"})," (AMPC). The question is, What\u2019s your mentality? How do you see yourself? When we say Christ has given your life a meaning, that\u2019s what we\u2019re talking about, because you\u2019ve come to a place where you realize life isn\u2019t for defeat; your journey is victory unto victory. That\u2019s our heritage in Christ."]}),"\n",(0,t.jsxs)(s.p,{children:["It makes no difference that the world is in crises; there should never be fear in you. Jesus said, ",(0,t.jsx)(s.em,{children:"\u201c\u2026be of good cheer; I have overcome the world\u201d"})," (John 16:33). He overcame the world for you. To be born again is to be born of God, and it says whatever is born of God is victorious over Satan, the world and its failing system. Hallelujah!"]}),"\n",(0,t.jsx)(s.p,{children:"Get excited when you face troubles or crises, because you\u2019re certain it\u2019ll all turn out for your good. This is because, in our kingdom, we win only, and make progress from glory to glory. We dominate this physical world with the principles of our heavenly Kingdom. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019m triumphant over the world, because I\u2019m born of God. When the fierce storms and pressures of life billow against me, I refuse to be moved, for I\u2019m more than a conqueror. I refuse to be overwhelmed by anything in this life, because the greater One lives in me and causes me to triumph always, and in every place. Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Romans 8:35-37; 1 John 4:3-4"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Get excited when you face troubles or crises, because you\u2019re certain it\u2019ll all turn out for your good. This is because, in our kingdom, we win only, and make progress from glory to glory."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 1:1-25; Deuteronomy 3-4",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 23:1-12; Exodus 24"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"glory-through-the-church-march-16th-2021",children:"GLORY THROUGH THE CHURCH March 16th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-16-glory-through-the-church.jpg",alt:"March 16th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Now unto him that is able to do exceeding abundantly above all that we ask or think, according to the power that worketh in us, Unto him be glory in the church by Christ Jesus throughout all ages, world without end. Amen (Ephesians 3:20-21)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"In the first chapter of the book of Ephesians, the Apostle Paul, by the Spirit, began to explain who Christ is, with respect to God, and who the Church is. He also went on to explain why the Church isn\u2019t a conglomeration of religious groups; rather, it\u2019s the Body of Christ, full of glory."}),"\n",(0,t.jsxs)(s.p,{children:["In Ephesians 3:6 NKJV, he explained ",(0,t.jsx)(s.em,{children:"\u201cthat the Gentiles should be fellow heirs, of the same body, and partakers of His promise in Christ through the gospel.\u201d"})," 1 Corinthians 12:27 tells us that we\u2019re the Body of Christ and members in particular. How did we become members of the Body of Christ? It was by being baptized into Christ at the New Birth. Our theme verse says, ",(0,t.jsx)(s.em,{children:"\u201cUnto him be glory in the church by Christ Jesus throughout all ages, world without end. Amen.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"The Greek word translated \u201cages\u201d is \u201cgenea\u201d; it means a generation. That means from generation to generation, the Father is glorified in the Church, by Jesus Christ. The desire and ministry of Jesus is to glorify the Father always, and this He does through the Church."}),"\n",(0,t.jsx)(s.p,{children:"Even in the face of diverse persecution and belligerent oppositions, we remain bold and courageous for and with the Gospel, because His glory is in, and upon us. With that glory, we triumph over sickness, diseases, poverty, and reign victoriously in the realms of life. The Church of Jesus Christ is glorious, triumphant, flourishing, and full of dominion, wisdom and power. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I belong to the Church of Jesus Christ, and I bring the Lord glory every day. Christ in me is the glory-life. I manifest the perfect glory of God, the power, beauty, and excellence of Christ that\u2019s inherent in my spirit. With that glory, I triumph over sickness, disease, poverty, and reign victoriously, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"John 17:22; Colossians 1:26-27"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"From generation to generation, the Father is glorified in the Church, by Jesus Christ. The desire and ministry of Jesus is to glorify the Father always, and this He does through the Church."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 1:26-56; Deuteronomy 5-7",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 23:13-22; Exodus 25"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"worship-with-knowledge-march-17th-2021",children:"WORSHIP WITH KNOWLEDGE March 17th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-17-worship-with-knowledge.jpg",alt:"March 17th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But the hour cometh, and now is, when the true worshippers shall worship the father in spirit and in truth: for the father seeketh such to worship him (John 4:23)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"When you really know Jesus, your worship or praise to Him becomes meaningful. It wouldn\u2019t just be because the song is melodious; rather, it\u2019ll effuse profusely from the deep recesses of your heart, in true fellowship and knowledge of His greatness and loving kindness. There may not be any instrumental music or melody while you\u2019re worshipping, but your heart will be so enraptured and entwined with Him, with outbursts of spirit songs in spiritual melody."}),"\n",(0,t.jsxs)(s.p,{children:["Remember, people praise, worship and pray in all religions. The question is, who is the object of their worship? Who are they praying to? What are they saying to Him? And what might be His response, if any? Your worship or praise has to come from a place of knowledge of the Word. The Word says, ",(0,t.jsx)(s.em,{children:"\u201cLet my prayer be set forth before thee as incense; and the lifting up of my hands be as the evening sacrifice\u201d"})," (Psalm 141:2). Also, in 1 Timothy 2:8, the Bible tells us, ",(0,t.jsx)(s.em,{children:"\u201cI will therefore that men pray every where, lifting up holy hands, without wrath and doubting.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"As you lift your hands in worship or prayer, you\u2019re offering to Him a sacrifice of sweet-smelling savour. Go ahead and practise this now. Worship the Father from your spirit and according to His Word, with your hands lifted up."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Father, I worship you according to your Word, and sing praises to you from my spirit. May your Name be glorified, praised and adored in my life and in all the earth. I lift my hands to you right now, in holiness and fellowship of the Spirit, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Hebrews 13:15; 1 Peter 2:9"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"When you really know Jesus, your worship or praise to Him becomes meaningful. It wouldn\u2019t just be because the song is melodious; rather, it\u2019ll effuse profusely from the deep recesses of your heart."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 1:57-80; Deuteronomy 8-10",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 23:23-39; Exodus 26"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"persecuted-for-his-sake-march-18th-2021",children:"PERSECUTED FOR HIS SAKE March 18th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-18-persecuted-for-his-sake.jpg",alt:"March 18th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Wherein they think it strange that ye run not with them to the same excess of riot, speaking evil of you (1 Peter 4:4)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"In 1 Peter 4, the Apostle Peter emphasizes that living in accordance to God\u2019s Word comes with a price of being ridiculed. Many will be surprised when you don\u2019t join them in the same wild and reckless living; and so they\u2019ll insult, vilify and speak evil of you."}),"\n",(0,t.jsxs)(s.p,{children:["The Amplified Classic translation of the opening text tells us, ",(0,t.jsx)(s.em,{children:"\u201cThey are astonished and think it very queer that you do not now run hand in hand with them in the same excesses of dissipation, and they abuse [you].\u201d"})," Irrespective of the evil or abuses they haul at you because you \u2018follow\u2019 Jesus, never be ashamed. Your association with Christ is the greatest honour ever."]}),"\n",(0,t.jsx)(s.p,{children:"The Bible says, \u201cFor it is better to suffer for doing good, if this should be God\u2019s will, than for doing evil\u201d (1 Peter 3:17). So, distinguish yourself for the Lord. Arm yourself with the same mind\u2014the mind of Christ. Don\u2019t be ashamed to stand up for the Lord no matter the persecutions. He stood up for you, and saved you; so, you must stand up for Him. Don\u2019t be concerned that you may be isolated or ostracized."}),"\n",(0,t.jsxs)(s.p,{children:["Jesus loves you even if all others forsake you, and according to the Word, He won\u2019t forsake you. He\u2019s in you and always with you: ",(0,t.jsx)(s.em,{children:"\u201c...lo, I am with you alway, even unto the end of the world. Amen\u201d"})," (Matthew 28:20). Read His words in Luke 21:33-36: ",(0,t.jsx)(s.em,{children:"\u201cHeaven and earth shall pass away: but my words shall not pass away. And take heed to yourselves, lest at any time your hearts be overcharged with surfeiting, and drunkenness, and cares of this life, and so that day come upon you unawares. For as a snare shall it come on all them that dwell on the face of the whole earth....\u201d"})]}),"\n",(0,t.jsxs)(s.p,{children:["We\u2019re in the end times, and the current happenings around the world are a fulfilment of these prophecies, right before our eyes. But here\u2019s a question you must answer in Hebrews 2:3: ",(0,t.jsx)(s.em,{children:"\u201cHow shall we escape, if we neglect so great salvation?....\u201d"})," Live according to God\u2019s Word and let nothing cause you to ignore or neglect your salvation. Jesus said in Matthew 5:11-12, ",(0,t.jsx)(s.em,{children:"\u201cBlessed are ye, when men shall revile you, and persecute you, and shall say all manner of evil against you falsely, for my sake. Rejoice, and be exceeding glad: for great is your reward in heaven: for so persecuted they the prophets which were before you.\u201d"})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019m undaunted and unmoved at the vilifying or slanderous words of the wicked, as I walk on in the Word, by the Spirit. I\u2019m the righteousness of God in Christ Jesus, His workmanship, created to produce fruits of righteousness. I\u2019ve crucified the flesh with its passions and desires; Satan has nothing in me! Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"2 Corinthians 7:1; Matthew 5:11-12"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Don\u2019t be ashamed to stand up for the Lord no matter the persecutions. He stood up for you, and saved you; so, you must stand up for Him. Don\u2019t be concerned that you may be isolated or ostracized."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 2:1-20; Deuteronomy 11-12",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 24:1-11; Exodus 27"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"holy-faultless-and-guiltless-march-19th-2021",children:"HOLY, FAULTLESS AND GUILTLESS March 19th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-19-prayer-is-sacrosanct.jpg",alt:"March 19th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"That he might sanctify and cleanse it with the washing of water by the word, That he might present it to himself a glorious church, not having spot, or wrinkle, or any such thing; but that it should be holy and without blemish (Ephesians 5:26-27)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["The Lord Jesus is coming for a glorious Church, one without blemish, a faultless Church. You say, \u201cis that possible?\u201d Absolutely! That\u2019s what His redemptive work was all about. Colossians 1:21-22 (CEV) says, ",(0,t.jsx)(s.em,{children:"\u201cYou used to be far from God. Your thoughts made you his enemies, and you did evil things. But his Son became a human and died. So God made peace with you, and now he lets you stand in his presence as people who are holy and faultless and innocent.\u201d"})]}),"\n",(0,t.jsxs)(s.p,{children:["Through the sacrifice of His own body as the sin-payment on your behalf, you now dwell in His presence. There\u2019s no enmity between you and the Father (Read Ephesians 2:13-16). How comforting it is to know the true nature and character of our heavenly Father! He brought us to Himself. James 1:5 says, He ",(0,t.jsx)(s.em,{children:"\u201c\u2026upbraideth not\u2026,\u201d"})," which means He doesn\u2019t reproach or criticize you. He doesn\u2019t blame you for anything. He doesn\u2019t count your sins against you (2 Corinthians 5:19)."]}),"\n",(0,t.jsxs)(s.p,{children:["You\u2019re free from accusation; you\u2019ve been justified. No wonder Paul got poetical in Romans 8:33-34 saying, ",(0,t.jsx)(s.em,{children:"\u201cWho shall lay anything to the charge of God\u2019s elect? It is God that justifieth. Who is he that condemneth? It is Christ that died, yea rather, that is risen again\u2026.\u201d"})," Then in verses 38 and 39, he climaxed, ",(0,t.jsx)(s.em,{children:"\u201cFor I am persuaded, that neither death, nor life, nor angels, nor principalities, nor powers, nor things present, nor things to come, nor height, nor depth, nor any other creature, shall be able to separate us from the love of God, which is in Christ Jesus our Lord.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"Christ Jesus already died for you; He was condemned in your place. Thus, no one should condemn you anymore (Romans 8:1). Christ is your righteousness (1 Corinthians 1:30). It doesn\u2019t matter who accuses you; you\u2019re holy, faultless and guiltless before God, because Jesus took responsibility for all your sins. He\u2019s the reason God doesn\u2019t charge you with wrong. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Father, I thank you for the ultimate sacrifice by which I now stand justified in your presence. I\u2019m the righteousness of God in Christ Jesus, unblameable and irreprovable in your sight. Thank you for ushering me into this new life of righteousness, where I reign and rule with Christ forever, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 Thessalonians 3:13; Romans 5:8-9"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"It doesn\u2019t matter who accuses you; you\u2019re holy, faultless and guiltless before God, because Jesus took responsibility for all your sins. He\u2019s the reason God doesn\u2019t charge you with wrong."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 2:21-52; Deuteronomy 13-15",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 24:12-22; Exodus 28"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"pray-for-world-leaders-march-20th-2021",children:"PRAY FOR WORLD LEADERS March 20th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-20-pray-for-world-leaders.jpg",alt:"March 20th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"I exhort therefore, that, first of all, supplications, prayers, intercessions, and giving of thanks, be made for all men; For kings, and for all that are in authority; that we may lead a quiet and peaceable life in all godliness and honesty (1 Timothy 2:1-2)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"It was all over the news some time ago how a man strangled his four-year-old child to death in a bathtub. After killing him, he screamed with tears, \u201cI don\u2019t know why I did it!\u201d Of course, he went to prison for it. In his words, he said, \u201cI felt a power beyond myself and I strangled my own son.\u201d"}),"\n",(0,t.jsx)(s.p,{children:"Also reported, was a sixteen-year-old boy who walked into his home, killed his mother and father with gunshots. Just standing there, bemused, he said he didn\u2019t know why he did it. You\u2019re talking about sons and daughters, men and women, husbands and wives, dads and moms from hell! Why are they from hell? It\u2019s because they\u2019re possessed of demons; evil spirits have entered into them to carry out such despicable acts."}),"\n",(0,t.jsx)(s.p,{children:"That\u2019s why God tells us to pray for leaders because they aren\u2019t left out of such demonic influence. You\u2019ve got to pray for them because an evil spirit can enter into them. The Bible tells us in Luke 22:3 that an evil spirit entered into Judas Iscariot. When that happened, he walked out of where Jesus and the disciples were, went to the chief priests to finalise arrangements to betray Jesus, the Son of God."}),"\n",(0,t.jsxs)(s.p,{children:["When Judas saw Jesus hanging on the Cross, he thought Jesus would miraculously come off the Cross, but He didn\u2019t. Matthew 27:3-5 tells us, ",(0,t.jsx)(s.em,{children:"\u201cWhen Judas, who had betrayed him, saw that Jesus was condemned, he was seized with remorse and returned the thirty silver coins to the chief priests and the elders\u2026So Judas threw the money into the temple and left. Then he went away and hanged himself.\u201d"})," What does that tell you? Satan caused him to do it."]}),"\n",(0,t.jsx)(s.p,{children:"That\u2019s what\u2019s happening to some leaders today; they\u2019re taking steps that are detrimental, damaging and harmful to their nations, people, businesses and the systems. You might say, \u201cI trust my leaders\u201d; but they can be influenced and overpowered by these demonic forces to sign policies or do despicable things they never planned to do. Therefore, you must pray for them."}),"\n",(0,t.jsx)(s.p,{children:"Pray that they\u2019ll have the will, desire, and boldness to do what\u2019s right in spite of pressures, and refuse to yield to corrupting and negative influences. It\u2019s not out of place to also pray for the removal of those who push their selfish or satanic agendas at the detriment of their people. Most importantly, pray for the salvation of men and leaders alike."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Father, I pray for my leaders today, that wisdom and understanding be granted them to lead in accordance with your divine will and plan, and to make the right decisions that\u2019ll benefit the people, and cause peace and prosperity to be established, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 Timothy 2:1-2; Ezekiel 33:6-7"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Some leaders today are taking steps that are detrimental, damaging and harmful to their nations, people, businesses and the systems. Therefore, you must pray for them."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 3:1-38; Deuteronomy 16-17",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 24:23-33; Exodus 29"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"christ-our-light-march-21st-2021",children:"CHRIST OUR LIGHT March 21st, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-21-christ-our-light.jpg",alt:"March 21st, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"In him was life; and the life was the light of men. And the light shineth in darkness; and the darkness comprehended it not (John 1:4-5)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["In John 8:12, Jesus said, ",(0,t.jsx)(s.em,{children:"\u201c\u2026I am the light of the world: he that followeth me shall not walk in darkness, but shall have the light of life.\u201d"})," Again, in John 9:5, He said, \u201cAs long as I am in the world, I am the light of the world.\u201d For anyone to live without Christ, therefore, is to live in gross darkness, utter hopelessness, confusion, and be void of beauty. Light defines; gives direction, beauty, and illumines."]}),"\n",(0,t.jsx)(s.p,{children:"You can\u2019t even know yourself outside Christ; only under His light is your true identity and personality revealed. The Bible says with Him is the fountain of life: in His light shall we see light (Psalm 36:9). Hallelujah!"}),"\n",(0,t.jsx)(s.p,{children:"If you use all the lights in the world to define your life, you\u2019re sunk, because you\u2019re sure to see the wrong things. But in Him, in His light, you see the glory of God. You see that you\u2019re excellent and full of glory. His light shows who you are, what you have, and your abilities in Christ. It shows you have the greater One living in you."}),"\n",(0,t.jsxs)(s.p,{children:["No wonder the Psamist said in Psalm 119:105, ",(0,t.jsx)(s.em,{children:"\u201cThy word is a lamp unto my feet, and a light unto my path.\u201d"})," Christ and His Word are one. Apart from showing you your endowments and blessings in Christ, the Word guides you in God\u2019s perfect will for your life, imparting to you the wisdom to fulfil it."]}),"\n",(0,t.jsx)(s.p,{children:"Through His Word, you receive guidance as to specific details about your life, your ministry, career, etc., and definite steps to take in actualizing your set goals. The Bible says Christ has been made unto us wisdom from God. Become more conscious of this, and you\u2019ll function with greater insight and knowledge, walking in success, health, prosperity, peace, and strength always!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"There\u2019re no uncertainties and confusion in my path, because Christ is my light, my wisdom, and my strength. I\u2019m walking in, and fulfiling God\u2019s perfect will for my life, established in the path of greatness and excellence forever, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"2 Corinthians 4:6; Psalm 119:130; Isaiah 2:5"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Christ and His Word are one. Through His Word, you receive guidance as to specific details about your life, your ministry, career, etc., and definite steps to take in actualizing your set goals."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 4:1-13; Deuteronomy 18-21",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 24:34-44; Exodus 30"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"marked-for-victory-and-success-march-22nd-2021",children:"MARKED FOR VICTORY AND SUCCESS March 22nd, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-22-marked-for-victory-and-success.jpg",alt:"March 22nd, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"And he shall be like a tree planted by the rivers of water, that bringeth forth his fruit in his season; his leaf also shall not wither; and whatsoever he doeth shall prosper (Psalm 1:3)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"As a child of God, you\u2019re called to live each day of your life in glory, in victory over every situation. You\u2019re marked for success. The triumphant life is your birth-right, because God has raised you up, and made you to sit together with Christ in heavenly places (Ephesians 2:6)."}),"\n",(0,t.jsxs)(s.p,{children:["You\u2019re seated far above the devil and the cohorts of darkness, and the negative influences of this world. Have the mentality that you can\u2019t be defeated or disadvantaged. Nothing in this world can successfully stand against you, for ",(0,t.jsx)(s.em,{children:"\u201c\u2026greater is he that is in you, than he that is in the world\u201d"})," (1 John 4:4). You\u2019ve been translated into the Kingdom of God\u2019s dear Son, who leads you in the triumphal excursions of life."]}),"\n",(0,t.jsxs)(s.p,{children:["What a life He\u2019s given us! Imagine what He tells us in our opening verse; the same truth is expressed in John 15:16 where He says, ",(0,t.jsx)(s.em,{children:"\u201cYe have not chosen me, but I have chosen you, and ordained you, that ye should go and bring forth fruit, and that your fruit should remain: that whatsoever ye shall ask of the Father in my name, he may give it you.\u201d"})," Jesus brought us into a life of power, fruitfulness and productivity. You\u2019ve been chosen to bring forth lasting results."]}),"\n",(0,t.jsxs)(s.p,{children:["This means He has made you efficient and effective; it\u2019s one of the beautiful results of having the Holy Spirit in your life. Acts 1:8 AMPC says, ",(0,t.jsx)(s.em,{children:"\u201cBut you shall receive power (ability, efficiency, and might) when the Holy Spirit has come upon you....\u201d"})," This is the mind-set with which you must approach the things you do. There\u2019s increased grace for you."]}),"\n",(0,t.jsx)(s.p,{children:"The Lord is at work in you, both to will and to do of His good pleasure (Philippians 2:13). He\u2019s your sufficiency (2 Corinthians 3:5); your \u201cable-to-do-it-ness.\u201d Every so often, declare that you\u2019re marked for success and victory; you produce excellent results. This is the life God has given you; it\u2019s a life of dominion, where you triumph gloriously, and honour Him with your works and fruits of righteousness."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019m marked for success and victory; I produce excellent results. I\u2019m chosen for a life of fruitfulness and ever-increasing productivity, bringing honour to Christ, through my labour of love and fruits of righteousness. Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Jeremiah 17:7-8; Psalm 92:12-14"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"As a child of God, you\u2019re called to live each day of your life in glory, in victory over every situation. You\u2019re marked for success. The triumphant life is your birth-right."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 4:14-44; Deuteronomy 22-24",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 24:45-51; Exodus 31"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"perfect-completion-march-23rd-2021",children:"PERFECT COMPLETION March 23rd, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-23-perfect-completion.jpg",alt:"March 23rd, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"For in him dwelleth all the fulness of the Godhead bodily. And ye are complete in him, which is the head of all principality and power (Colossians 2:9-10)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"The totality of divinity resides in Jesus Christ. When you see Jesus, you\u2019re looking at the fullness of the God-head; He\u2019s the house of God in totality. Whether or not you agree with this doesn\u2019t make any difference. You may think of Him and say He\u2019s just a man, but He is Himself God!"}),"\n",(0,t.jsx)(s.p,{children:"\u201cFullness\u201d in our theme scripture means \u201ca completeness,\u201d where nothing that\u2019s necessary is out; where there\u2019s no shortage; a perfect completion. And something most amazing about this is what we read in the latter part: \u201cYou\u2019re complete in Him, who is the head of all principality and power.\u201d"}),"\n",(0,t.jsxs)(s.p,{children:["When God looks at you, He sees everything you require for life and godliness in you. 2 Peter 1:3 says, ",(0,t.jsx)(s.em,{children:"\u201cAccording as his divine power hath given unto us all things that pertain unto life and godliness, through the knowledge of him that hath called us to glory and virtue.\u201d"})," This is the reality. Refuse to live with a lack mentality, as though there\u2019re certain things you don\u2019t have. Take off the limits."]}),"\n",(0,t.jsx)(s.p,{children:"Refuse to think that your completeness is in the government, your place of work or any human structure, organization or association. Your completeness is in Christ. You\u2019re sufficient in Christ\u2019s sufficiency. Become totally inundated with this consciousness."}),"\n",(0,t.jsx)(s.p,{children:"Your confession, every so often, should be, \u201cFather, I thank you, because I have all things! I\u2019m complete in Christ; there\u2019s no shortage in my life, but completeness.\u201d Do this consciously and continually, and see your life blossom in righteousness, glory, wisdom and dominion of Christ, beyond your imagination. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"Christ is my all; I\u2019m complete in Him who is the head of all principality and power; there\u2019s no shortage in my life. I\u2019m full of ideas, inspiration, with wealth, prosperity and all the blessings of the Kingdom; and out of that abundance, I impact my world. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Colossians 1:16-19; 1 Corinthians 3:21-22"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"When God looks at you, He sees everything you require for life and godliness in you. This is the reality. Refuse to live with a lack mentality, as though there\u2019re certain things you don\u2019t have."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 5:1-16; Deuteronomy 25-27",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 25:1-13; Exodus 32"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"receiving-forgiveness-is-an-act-of-faith-march-24th-2021",children:"RECEIVING FORGIVENESS IS AN ACT OF FAITH March 24th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-24-receiving-forgiveness-is-an-act-of-faith.jpg",alt:"March 24th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But if we walk in the light, as he is in the light, we have fellowship one with another, and the blood of Jesus Christ his Son cleanseth us from all sin (1 John 1:7)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Did you know that in the New Testament, God didn\u2019t tell us to ask Him for forgiveness? Asking Him for forgiveness is like asking Him to save you. Think about it: If you do ask Him for forgiveness, when will you know He has answered?"}),"\n",(0,t.jsx)(s.p,{children:"Likewise, if you\u2019re asking Him to save your soul, how will you know He has saved you? You\u2019ll never know, because there\u2019s nothing for Him to do about saving you that He hasn\u2019t already done. It\u2019s the same thing with forgiveness; you receive forgiveness; you don\u2019t ask for it, because the blood of Jesus Christ was shed for the remission of your sins."}),"\n",(0,t.jsx)(s.p,{children:"Some Christians are always asking God for forgiveness, and because they\u2019re asking for something that\u2019s been provided for, they don\u2019t apply faith. You\u2019re to receive forgiveness as an act of your faith. Afterwards, live above that situation through the power of the Holy Spirit and the Word."}),"\n",(0,t.jsxs)(s.p,{children:["You\u2019ve already been washed and purged of your sins. Colossians 1:21-22 says, ",(0,t.jsx)(s.em,{children:"\u201cAnd you, that were sometime alienated and enemies in your mind by wicked works, yet now hath he reconciled In the body of his flesh through death, to present you holy and unblameable and unreproveable in his sight.\u201d"})," This happened before you ever did anything right or wrong. Therefore, refuse to be sin-conscious. You\u2019re the righteousness of God in Christ Jesus; think and live righteousness."]}),"\n",(0,t.jsx)(s.p,{children:"Our opening text says if we walk in the light (the Word) as He is in the light, the blood of Jesus automatically cleanses us from all sin. Since this is true, you don\u2019t have to be confessing your sins all the time. If you do something wrong, simply say, \u201cLord, I\u2019m sorry for what I did; I receive forgiveness in Jesus\u2019 Name\u201d; and that\u2019s it. You might ask, \u201cIs it that simple?\u201d Yes, it is. In Christ, we have automatic cleansing from sin; it\u2019s part of the redemptive work of His blood in our behalf."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019m cleansed and purified. I\u2019m justified by the grace of Jesus Christ, and I walk in His righteousness, free from the accusation of the evil one, and at liberty to serve the Lord, Christ! Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 Corinthians 6:11; Romans 8:1"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Some Christians are always asking God for forgiveness, and because they\u2019re asking for something that\u2019s been provided for, they don\u2019t apply faith. You\u2019re to receive forgiveness as an act of your faith."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 5:17-39; Deuteronomy 28",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 25:14-30; Exodus 33"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"dominate-your-world-march-25th-2021",children:"DOMINATE YOUR WORLD March 25th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-25-dominate-your-world.jpg",alt:"March 25th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Wherefore if ye be dead with Christ from the rudiments of the world, why, as though living in the world, are ye subject to ordinances (Colossians 2:20)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Our opening verse lets us know that we\u2019re not subject to the principles of this world. Jesus dominated this world; He lived above the decadence of the world, the corruption and failure of His society. He lived far above, and beyond the government and systems of the day when He walked the earth. That\u2019s the life He\u2019s given us: one that\u2019s not subject to, but lords over, the rudiments of this world."}),"\n",(0,t.jsx)(s.p,{children:"He\u2019s made us self-sufficient, to function above and beyond the systems of this world. He said you\u2019re in the world, but not of the world. That means you\u2019re to dominate the systems and corrupting influences in today\u2019s world. Many around you may be influenced, and provoked to compromise as a result of the deceit, corruption and wickedness in today\u2019s world, but not you. You hail from God\u2019s Kingdom, and your life is built on, and regulated by, His truth only."}),"\n",(0,t.jsxs)(s.p,{children:["Keep learning God\u2019s Word. It\u2019ll make you think and talk like Jesus. In John 16:33, He said, ",(0,t.jsx)(s.em,{children:"\u201cThese things I have spoken unto you, that in me ye might have peace. In the world ye shall have tribulation: but be of good cheer; I have overcome the world.\u201d"})," Have the same consciousness, because He overcame the world for you. Although He lived in a world plagued by gross darkness, deceit, economic hardships, sickness, oppression, deaths, disasters, etc., He had a victor\u2019s mindset; the kind He expects you to function with."]}),"\n",(0,t.jsx)(s.p,{children:"When you face challenges, persecutions, distressing situations, turbulent times, trials, and temptations, be unruffled; because in Christ, you have overcome the world (1 John 4:4). Let this consciousness reflect even in your prayer-language. It makes no difference the challenges you face; when you pray, declare that in the Name of Jesus, you\u2019ve overcome the world and its systems; its hatred, deceit, poverty, corruption, depression, falsehood and antichrist spirit. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I live and function above and beyond the systems of this world; I\u2019m independent of the hardships, corruption, evil and deceit in the world because I live in Christ, in His Word, and in the dominion of the Spirit, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 John 5:4-5; Psalm 8:4-6"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Jesus dominated this world; He lived above the decadence of the world, the corruption and failure of His society. That\u2019s the life He\u2019s given us: one that\u2019s not subject to, but lords over, the rudiments of this world."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 6:1-16; Deuteronomy 29-30",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 25:31-46; Exodus 34"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"clothed-in-glory-march-26th-2021",children:"CLOTHED IN GLORY March 26th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-26-clothed-in-glory.jpg",alt:"March 26th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Moreover whom he did predestinate, them he also called: and whom he called, them he also justified: and whom he justified, them he also glorified (Romans 8:30)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["How beautiful this is! We\u2019ve been called, justified and glorified. Your life is full of glory. That\u2019s the reason you can manifest the glory of God; it\u2019s what He called you to do: ",(0,t.jsx)(s.em,{children:"\u201cBut you are a chosen race, a royal priesthood, a dedicated nation, [God\u2019s] own purchased, special people, that you may set forth the wonderful deeds and display the virtues and perfections of Him Who called you out of darkness into His marvelous light\u201d"})," (1 Peter 2:9 AMPC)."]}),"\n",(0,t.jsxs)(s.p,{children:["Jesus said in John 17:22, ",(0,t.jsx)(s.em,{children:"\u201cAnd the glory which thou gavest me I have given them; that they may be one, even as we are one.\u201d"})," It\u2019s important that you see yourself in this light. See yourself in, and as, the glory of God. When Adam and Eve disobeyed God in the Garden, their fall made them \u201csee\u201d that they were naked, and they became ashamed and afraid (Genesis 3:7). When God called out to Adam in the Garden, he replied, ",(0,t.jsx)(s.em,{children:"\u201c\u2026I heard thy voice in the garden, and I was afraid, because I was naked; and I hid myself\u201d"})," (Genesis 3:10)."]}),"\n",(0,t.jsxs)(s.p,{children:["The Lord\u2019s response to him was, ",(0,t.jsx)(s.em,{children:"\u201c\u2026 Who told thee that thou wast naked...?\u201d"})," (Genesis 3:11). Adam saw his guilt and his shame, but that\u2019s not what God wants you to see. 2 Corinthians 5:17 says*, \u201c\u2026if any man be in Christ, he is a new creature: old things are passed away; behold, all things are become new.\u201d* The word \u201cbehold\u201d is the Greek, \u201cidou,\u201d which is akin to the Hebrew word, \u201cchazah.\u201d It means to become spiritually aware, to see, and come alive to what you see. God wants you to see that you\u2019re clothed with glory."]}),"\n",(0,t.jsxs)(s.p,{children:["You were born to reign and manifest the glory of the Kingdom of God in the earth. Romans 8:19 says, ",(0,t.jsx)(s.em,{children:"\u201cFor the earnest expectation of the creature waiteth for the manifestation of the sons of God.\u201d"})," What a life! The same way Jesus came to the earth to express God and manifest His glory, you\u2019ve been chosen of God to show forth His glory. You\u2019ve been called to display God."]}),"\n",(0,t.jsxs)(s.p,{children:["Read Colossians 1:27: ",(0,t.jsx)(s.em,{children:"\u201cTo whom God would make known what is the riches of the glory of this mystery among the Gentiles; which is Christ in you, the hope of glory.\u201d"})," This means glory has been realized. Therefore, SEE that you\u2019ve been glorified. There\u2019s no reason to be afraid or run from God. He\u2019s taken away all guilt, reproach and shame from your life. He\u2019s justified you. Now, He wants you to live as His glorified one\u2014in His righteousness\u2014as a champion. Hallelujah!"]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I\u2019ve been chosen of God to show forth His glory; therefore, I see myself in the glory, walking in the glory and displaying God\u2019s righteousness to my world. Hallelujah!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"1 Peter 2:9; 2 Corinthians 3:18"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"How beautiful this is! We\u2019ve been called, justified and glorified. Your life is full of glory. That\u2019s the reason you can manifest the glory of God; it\u2019s what He called you to do."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 6:17-49; Deuteronomy 31-32",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 26:1-13; Exodus 35"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"more-than-letters-march-27th-2021",children:"MORE THAN LETTERS March 27th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-27-more-than-letters.jpg",alt:"March 27th, 2021"})}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Not that we are sufficient of ourselves to think anything as of ourselves; but our sufficiency is of God; Who also hath made us able ministers of the new testament; not of the letter, but of the spirit: for the letter killeth, but the spirit giveth life (2 Corinthians 3:5-6)."})})}),"\n"]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Not that we are sufficient of ourselves to think anything as of ourselves; but our sufficiency is of God; Who also hath made us able ministers of the new testament; not of the letter, but of the spirit: for the letter killeth, but the spirit giveth life"})})," ",(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"(2 Corinthians 3:5-6)."})})]}),"\n",(0,t.jsxs)(s.p,{children:["The Word of God is more than letters; it\u2019s spirit and life. The letter kills, but the Spirit gives life. Jesus said, ",(0,t.jsx)(s.em,{children:"\u201cIt is the spirit that quickeneth; the flesh profiteth nothing: the words that I speak unto you, they are spirit, and they are life\u201d"})," (John 6:63). The Word builds, lifts, and transforms us from glory to glory. It\u2019s the reason we emphasize meditating on the Word of God."]}),"\n",(0,t.jsx)(s.p,{children:"Meditation gets the Word into your spirit to become personal to you. As you study or listen to God\u2019s Word, every time there\u2019s a stirring in your spirit to speak forth words on the basis of what you\u2019re studying or being taught, don\u2019t suppress it. Speak forth. Each time you hear the Word, it\u2019s God talking to you, but He always wants you to respond; that\u2019s where the glory is!"}),"\n",(0,t.jsx)(s.p,{children:"Until you respond to God\u2019s Word, it won\u2019t produce any results. The Word will be mere letters to you. But the Word in your mouth is God talking; it\u2019s the sword of the Spirit with which you cut the devil down and prevail over the adversities of life."}),"\n",(0,t.jsxs)(s.p,{children:["That transformation you desire in your finances, health and family is dependent on you, your response to the Word. Psalm 119:89 says, ",(0,t.jsx)(s.em,{children:"\u201cFor ever, O LORD, thy word is settled in heaven.\u201d"})," His Word is settled in heaven, but you must settle it in your life through your affirmations of the Word. Hallelujah!"]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Father, your Word is my life, and the light that guides my path in victory and glory. I\u2019ll never walk in darkness, because my spirit is enlightened by the Holy Spirit to know and take a hold of my inheritance in Christ, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Isaiah 55:11; Acts 20:32; 2 Timothy 3:16-17"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"The Word of God is more than letters; it\u2019s spirit and life. The Word builds, lifts, and transforms us from glory to glory. It\u2019s the reason we emphasize meditating on the Word of God."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 7:1-35; Deuteronomy 33-34",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 26:14-25; Exodus 36"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"serve-the-lord-heartily-march-28th-2021",children:"SERVE THE LORD HEARTILY March 28th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-28-serve-the-lord-heartily.jpg",alt:"March 28th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"And Samuel spake unto all the house of Israel, saying, If ye do return unto the LORD with all your hearts, then put away the strange gods and Ashtaroth from among you, and prepare your hearts unto the LORD, and serve him only: and he will deliver you out of the hand of the Philistines (1 Samuel 7:3)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["We\u2019re in the final moments of the last days, and it\u2019s important that you\u2019re focused on the Lord, serving Him heartily. The Bible says, ",(0,t.jsx)(s.em,{children:"\u201cYou may as well know this too\u2026that in the last days it is going to be very difficult to be a Christian\u201d"})," (2 Timothy 3:1 TLB). Notice it doesn\u2019t say, \u201cIt shall be impossible to be a Christian,\u201d but \u201cdifficult.\u201d So, it\u2019s telling you to serve the Lord, regardless and independent of circumstances or persecution."]}),"\n",(0,t.jsx)(s.p,{children:"This isn\u2019t the time to hide your Christian identity. Nothing whatsoever should be viable enough to take your attention away from the Lord and His Kingdom. Some are only fervent in serving the Lord when everything seems rosy, when their business is thriving and their finances are booming. But the true test comes when trouble strikes, and they get discouraged, and suddenly lose their fervour."}),"\n",(0,t.jsx)(s.p,{children:"Some others only serve the Lord during times of challenges. But as soon as they have a miracle and are out of the dilemma, their commitment to the Lord immediately wanes. Something is wrong. Don\u2019t let that happen to you. Be on fire for the Lord all the time. Be instant in season, and out of season."}),"\n",(0,t.jsxs)(s.p,{children:["Let nothing compete with, or inhibit your love for and commitment to Christ. The Bible says, ",(0,t.jsx)(s.em,{children:"\u201cTherefore, my beloved brethren, be ye stedfast, unmoveable, always abounding in the work of the Lord\u2026\u201d"})," (1 Corinthians 15:58). When everything is going smoothly, be steadfast. And when it\u2019s a period of adversity, serve the Lord heartily still. The Bible says, ",(0,t.jsx)(s.em,{children:"\u201cIf thou faint in the day of adversity, thy strength is small\u201d"})," (Proverbs 24:10). Colossians 3:23 tells us, ",(0,t.jsx)(s.em,{children:"\u201cAnd whatsoever ye do, do it heartily, as to the Lord\u2026.\u201d"})," Praise God!"]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Lord, I\u2019m always active in my service to you, in truth, with all my heart. I refuse to be slothful in the dealings of the Spirit; I\u2019m fervent, serving you wholeheartedly, rejoicing in hope, and patient in times of trouble. I remain steadfast and unmovable, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Romans 12:11-12; 1 Corinthians 15:58"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"We\u2019re in the final moments of the last days, and it\u2019s important that you\u2019re focused on the Lord, serving Him heartily. Nothing whatsoever should be viable enough to take your attention away from the Lord and His Kingdom."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 7:36-50; Joshua 1-2",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 26:26-35; Exodus 37"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"its-sooner-than-you-think-march-29th-2021",children:"IT\u2019S SOONER THAN YOU THINK March 29th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-29-its-sooner-than-you-think.jpg",alt:"March 29th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Behold, I come quickly: blessed is he that keepeth the sayings of the prophecy of this book (Revelation 22:7)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Some have wondered why we preach the Gospel the way we do, all around the world, with so much urgency, fervency and passion. It\u2019s because the Master gave us a mandate to evangelize the world before He comes back, leading men and women into righteousness."}),"\n",(0,t.jsxs)(s.p,{children:["In Mathew 28:19, He said, \u201c",(0,t.jsx)(s.em,{children:"Go ye therefore, and teach all nations, baptizing them in the name of the Father, and of the Son, and of the Holy Ghost.\u201d"})," He gave us a message for the whole world about what He came to do in the earth, so that when He comes back, many will be prepared for Him, and go with Him. Hallelujah!"]}),"\n",(0,t.jsx)(s.p,{children:"This is our inspiration and the urgency of the moment. We\u2019re not just trying to get people excited about Christianity; it\u2019s about Jesus Christ and His living message: He came to die for the whole world, and God raised Him from the dead. He ascended into heaven, and He\u2019s alive today, and will come back any time from now."}),"\n",(0,t.jsx)(s.p,{children:"How ready are you for His return? If you\u2019re still wondering when, and how soon He is coming back, I can tell you this: it\u2019s a lot sooner than many realize, judging from the timelines revealed to us in Scriptures."}),"\n",(0,t.jsx)(s.p,{children:"All the things He told us will happen before He comes are happening exactly the way He said they would. There aren\u2019t too many signs or events left to be fulfilled before the Master\u2019s return. This is why we must keep preaching the Gospel, preparing men and women all over the world for the Rapture. He was once offered to bear the sins of many; and unto them that look for Him shall He appear the second time without sin, unto salvation (Hebrews 9:28). Therefore, be prepared and ready for Him."}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Prayer"}),(0,t.jsx)(s.p,{children:"Dear Father, through the preaching and teaching of the Gospel of righteousness, your Kingdom is established in the earth and in the hearts of men. Many are snatched from destruction and damnation, into the glorious liberty of the sons of God. Thank you for the increased grace upon the Church to proclaim liberty in all the earth, in these last days, in Jesus\u2019 Name. Amen."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Acts 1:11; 1 Corinthians 16:22; Revelation 22:20"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"How ready are you for His return? If you\u2019re still wondering when, and how soon He is coming back, I can tell you this: it\u2019s a lot sooner than many realize."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 8:1-21; Joshua 3-4",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 26:36-46; Exodus 38"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"the-sanctifier-in-you-march-30th-2021",children:"THE SANCTIFIER IN YOU March 30th, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-30-the-sanctifier-in-you.jpg",alt:"March 30th, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"But of him are ye in Christ Jesus, who of God is made unto us wisdom, and righteousness, and sanctification, and redemption (1 Corinthians 1:30)."})})}),"\n"]}),"\n",(0,t.jsx)(s.p,{children:"Besides being your wisdom, righteousness, and redemption (riddance), Christ is also your sanctification. The word sanctification used in our opening verse refers to the act or process of making holy or setting apart unto God for His eternal purpose. It\u2019s translated from the Greek word \u201chagiasmos\u201d which means, \u201ca purifier.\u201d So, the sanctifier has come to live in you."}),"\n",(0,t.jsx)(s.p,{children:"Christ isn\u2019t in you, trying to sanctify you; He has sanctified you, and continues to sanctify everything you do. He\u2019s your separator; He has separated you from the rest of the world, from sin and evil, unto the glorious liberty of the sons of God. Hallelujah! Just as you\u2019re required to activate wisdom by speaking wisdom, you\u2019re also required to speak sanctification\u2014words that cleanse and purify."}),"\n",(0,t.jsxs)(s.p,{children:["Recall what Paul said in 1 Timothy 4:4-5: ",(0,t.jsxs)(s.em,{children:["\u201cFor every creature of God is good, and nothing to be refused, if it be received with thanksgiving: ",(0,t.jsx)(s.strong,{children:"For it is sanctified by the word of God and prayer"}),".\u201d"]})," You can purify or sanctify anything that belongs to you with the Word of God and prayer."]}),"\n",(0,t.jsxs)(s.p,{children:["The Lord Jesus said in John 15:3, ",(0,t.jsx)(s.em,{children:"\u201cNow ye are clean through the word which I have spoken unto you.\u201d"})," Jesus has gone to heaven, but He lives in, and through you today. You\u2019re the one bearing those wonderful sanctifying words today. Keep speaking words of blessings, to cleanse and sanctify your environment. As you speak, people are purified; they\u2019re transformed from glory to another."]}),"\n",(0,t.jsxs)(s.p,{children:["Hebrews 2:11 says, ",(0,t.jsx)(s.em,{children:"\u201cFor both he that sanctifieth and they who are sanctified are all of one: for which cause he is not ashamed to call them brethren.\u201d"})," Christ the sanctifier is in you; He\u2019s one with your spirit now (1 Corinthians 6:17). And through you, He\u2019s carrying out His ministry of sanctifying all things to Himself."]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I rejoice evermore in Christ, my sanctifier, my righteousness, the One who washed me and presented me holy and unblameable before the Father in love. My life is for the glory of God. I\u2019m the testimony of the Father\u2019s love, the proof of His equity. Blessed be God!"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Hebrews 2:11; 1 Thessalonians 5:23"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Christ has sanctified you, and continues to sanctify everything you do. Just as you\u2019re required to activate wisdom by speaking wisdom, you\u2019re also required to speak sanctification\u2014words that cleanse and purify."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 8:22-39; Joshua 5-6",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 26:47-56; Exodus 39"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.h2,{id:"the-new-and-living-way-march-31st-2021",children:"THE NEW AND LIVING WAY March 31st, 2021"}),"\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/rhapsody-pastor-chris-day-31-the-new-and-living-way.jpg",alt:"March 31st, 2021"})}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(s.blockquote,{children:["\n",(0,t.jsx)(s.p,{children:(0,t.jsx)(s.em,{children:(0,t.jsx)(s.strong,{children:"Having therefore, brethren, boldness to enter into the holiest by the blood of Jesus, By a new and living way, which he hath consecrated for us, through the veil, that is to say, his flesh (Hebrews 10:19-20)."})})}),"\n"]}),"\n",(0,t.jsxs)(s.p,{children:["How awesome this is! Jesus opened for us a new and living way to the Father; He took off the veil to the Father\u2019s presence and ushered us in. Blessed be God! Remember His words in John 14:6: ",(0,t.jsx)(s.em,{children:"\u201c\u2026I am the way, the truth, and the life: no man cometh unto the Father, but by me.\u201d"})]}),"\n",(0,t.jsx)(s.p,{children:"Recall that in Genesis, Adam had sinned against God and was driven away from the presence of God, together with his wife, Eve. They\u2019d been in fellowship with God in the Garden of Eden, but the Bible says God sent an angel with a flaming sword to stay at the gate of the Garden to disallow them from re-entering the garden."}),"\n",(0,t.jsxs)(s.p,{children:["Suddenly, the rich fellowship man once had with God was over. That was the beginning of religion. Man began to do everything he could to get that divine presence again. All the religions of the world today are seeking the same thing: the divine presence. Man had no way to get back into that presence, until Jesus came. He made a startling statement which we read earlier. He said, ",(0,t.jsx)(s.em,{children:"\u201c...I am the WAY\u2026no one comes to the Father, but by me!\u201d"})," (John 14:6)."]}),"\n",(0,t.jsx)(s.p,{children:"This was what Adam needed. This was what the Patriarchs of the Old Testament all needed\u2014the way to the Father\u2019s presence. Jesus is that way. When He died on the Cross, the Bible says the thick veil that separated the temple from the Most Holy Place was torn in two, from top to bottom (Matthew 27:51), making the presence of God accessible."}),"\n",(0,t.jsx)(s.p,{children:"Today, there\u2019s no veil, no separation anymore between you and the Father. You live in His presence, and the Bible says, \u201cIn His presence is fullness of joy and pleasures evermore\u201d (Psalm 16:11). If you haven\u2019t found your way into His presence, if you\u2019re not yet born again, there\u2019s no need to wallow in sin, defeat and ignorance anymore. There\u2019s no need searching for the way; Jesus is the Way. All you have to do is confess His lordship over your life, and your spirit will be recreated to begin an exciting life in God\u2019s presence for eternity. Hallelujah!"}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Confession"}),(0,t.jsx)(s.p,{children:"I was born into God\u2019s presence to live eternally in joy, glory and pleasures evermore. My life is upward and forward, because in His presence is the path of life; His light illuminates and all I see is blessings and the glorious benefits of my inseparable oneness with Christ. Glory to His Name forever."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Further Study"}),(0,t.jsx)(s.p,{children:"Ephesians 2:18-19; Romans 5:1-2"})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Excerpt"}),(0,t.jsx)(s.p,{children:"Jesus opened for us a new and living way to the Father; He took off the veil to the Father\u2019s presence and ushered us in. You live in His presence."})]}),"\n",(0,t.jsxs)(n,{children:[(0,t.jsx)("summary",{children:"Bible Reading Plan"}),(0,t.jsxs)(s.p,{children:[(0,t.jsx)(s.strong,{children:"Annual"})," : Luke 8:40-56; Joshua 7-9",(0,t.jsx)(s.br,{}),"\n",(0,t.jsx)(s.strong,{children:"Bi-Annual"})," : Matthew 26:57-68; Exodus 40"]})]}),"\n",(0,t.jsx)(s.hr,{}),"\n",(0,t.jsx)(s.hr,{})]})}function u(e={}){const{wrapper:s}={...(0,r.R)(),...e.components};return s?(0,t.jsx)(s,{...e,children:(0,t.jsx)(l,{...e})}):l(e)}},28453:(e,s,n)=>{n.d(s,{R:()=>o,x:()=>h});var t=n(96540);const r={},i=t.createContext(r);function o(e){const s=t.useContext(i);return t.useMemo((function(){return"function"==typeof e?e(s):{...s,...e}}),[s,e])}function h(e){let s;return s=e.disableParentContext?"function"==typeof e.components?e.components(r):e.components||r:o(e.components),t.createElement(i.Provider,{value:s},e.children)}}}]);