"use strict";(self.webpackChunkrhapha_base=self.webpackChunkrhapha_base||[]).push([[3308],{3100:(e,n,t)=>{t.r(n),t.d(n,{assets:()=>h,contentTitle:()=>o,default:()=>u,frontMatter:()=>r,metadata:()=>a,toc:()=>l});var s=t(74848),i=t(28453);const r={title:"May"},o=void 0,a={id:"rhaphsody/2022/may",title:"May",description:"CELEBRATE HIS GOODNESS EVERY DAY May 1st, 2022",source:"@site/docs/rhaphsody/2022/may.md",sourceDirName:"rhaphsody/2022",slug:"/rhaphsody/2022/may",permalink:"/rhapha-base/rhaphsody/2022/may",draft:!1,unlisted:!1,editUrl:"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/docs/rhaphsody/2022/may.md",tags:[],version:"current",frontMatter:{title:"May"},sidebar:"tutorialSidebar",previous:{title:"March",permalink:"/rhapha-base/rhaphsody/2022/march"},next:{title:"November",permalink:"/rhapha-base/rhaphsody/2022/november"}},h={},l=[{value:"CELEBRATE HIS GOODNESS EVERY DAY May 1st, 2022",id:"celebrate-his-goodness-every-day-may-1st-2022",level:2},{value:"TRUE PRAISE AND WORSHIP May 2nd, 2022",id:"true-praise-and-worship-may-2nd-2022",level:2},{value:"TRAIN YOURSELF TO PRAY...... May 3rd, 2022",id:"train-yourself-to-pray-may-3rd-2022",level:2},{value:"PROTECT YOUR HEART May 4th, 2022",id:"protect-your-heart-may-4th-2022",level:2},{value:"SPEAKING IN TONGUES IS A GREAT BLESSING May 5th, 2022",id:"speaking-in-tongues-is-a-great-blessing-may-5th-2022",level:2},{value:"HIS LOVE MANIFESTED IN YOU May 6th, 2022",id:"his-love-manifested-in-you-may-6th-2022",level:2},{value:"SOUNDNESS OF HEALTH May 7th, 2022",id:"soundness-of-health-may-7th-2022",level:2},{value:"PRACTISE BEING ALONE WITH GOD May 8th, 2022",id:"practise-being-alone-with-god-may-8th-2022",level:2},{value:"THE POWER IS RELEASED WHEN YOU SPEAK May 9th, 2022",id:"the-power-is-released-when-you-speak-may-9th-2022",level:2},{value:"POWER OF A STRONG DESIRE May 10th, 2022",id:"power-of-a-strong-desire-may-10th-2022",level:2},{value:"PROVE YOUR LOVE BY WINNING SOULS May 11th, 2022",id:"prove-your-love-by-winning-souls-may-11th-2022",level:2},{value:"CHRISTIANITY AND THE RESURRECTION May 12th, 2022",id:"christianity-and-the-resurrection-may-12th-2022",level:2},{value:"WE DON\u2019T LIVE BY BLOOD May 13th, 2022",id:"we-dont-live-by-blood-may-13th-2022",level:2},{value:"TO KNOW HIM IS TO LOVE HIM May 14th, 2022",id:"to-know-him-is-to-love-him-may-14th-2022",level:2},{value:"THE SUPPLY ZONE May 15th, 2022",id:"the-supply-zone-may-15th-2022",level:2},{value:"MANDATE TO SHINE May 16th, 2022",id:"mandate-to-shine-may-16th-2022",level:2},{value:"TRANSFORMATION BY THE WORD May 17th, 2022",id:"transformation-by-the-word-may-17th-2022",level:2},{value:"LET\u2019S GIVE HIM WHAT HE WANTS May 18th, 2022",id:"lets-give-him-what-he-wants-may-18th-2022",level:2},{value:"PROTECT YOUR HEART WITH GOD\u2019S TRUTH May 19th, 2022",id:"protect-your-heart-with-gods-truth-may-19th-2022",level:2},{value:"WE LIVE IN HIS PRESENCE May 20th, 2022",id:"we-live-in-his-presence-may-20th-2022",level:2},{value:"PRAYING FOR OUR EVANGELICAL WORK May 21st, 2022",id:"praying-for-our-evangelical-work-may-21st-2022",level:2},{value:"HIS ETERNAL GLORY IN US May 22nd, 2022",id:"his-eternal-glory-in-us-may-22nd-2022",level:2},{value:"A MORE EXCELLENT WAY TO GIVE PRAISE May 23rd, 2022",id:"a-more-excellent-way-to-give-praise-may-23rd-2022",level:2},{value:"CALLED TO LEAD May 24th, 2022",id:"called-to-lead-may-24th-2022",level:2},{value:"LOVE LIKE CHRIST May 25th, 2022",id:"love-like-christ-may-25th-2022",level:2},{value:"WE ONLY GET STRONGER May 26th, 2022",id:"we-only-get-stronger-may-26th-2022",level:2},{value:"THE WALK OF A CHRISTIAN May 27th, 2022",id:"the-walk-of-a-christian-may-27th-2022",level:2},{value:"MUSIC IS BEYOND LYRICS May 28th, 2022",id:"music-is-beyond-lyrics-may-28th-2022",level:2},{value:"THE CHURCH IS GOD\u2019S SECURITY SYSTEM May 29th, 2022",id:"the-church-is-gods-security-system-may-29th-2022",level:2},{value:"GRACE HEAPED UPON GRACE May 30th, 2022",id:"grace-heaped-upon-grace-may-30th-2022",level:2},{value:"BE VISIONARY WITH THE GOSPEL May 31st, 2022",id:"be-visionary-with-the-gospel-may-31st-2022",level:2}];function d(e){const n={blockquote:"blockquote",br:"br",em:"em",h2:"h2",hr:"hr",img:"img",p:"p",strong:"strong",...(0,i.R)(),...e.components},{Details:t}=n;return t||function(e,n){throw new Error("Expected "+(n?"component":"object")+" `"+e+"` to be defined: you likely forgot to import, pass, or provide it.")}("Details",!0),(0,s.jsxs)(s.Fragment,{children:[(0,s.jsx)(n.h2,{id:"celebrate-his-goodness-every-day-may-1st-2022",children:"CELEBRATE HIS GOODNESS EVERY DAY May 1st, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-1-celebrate-his-goodness-every-day.jpg",alt:"May 1st, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"For they considered not the miracle of the loaves: for their heart was hardened (Mark 6:52)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["In Mark 6, the Bible tells us how the disciples of Jesus were gripped with fear when they saw Him walking on the water. Why were they afraid? We find the answer in our theme verse: ",(0,s.jsx)(n.em,{children:"\u201c\u2026they considered not the miracle of the loaves: for their heart was hardened.\u201d"})]}),"\n",(0,s.jsx)(n.p,{children:"They had seen several miracles with Jesus and the latest of such miracles was that of multiplying bread and fish to feed thousands of people (Read Matthew 14:13-21). Yet, it didn\u2019t seem to mean anything to them; they were still hardened in their hearts. Some are like that in the Church today; not considering as they should, the miracles of Christ, performed through the Spirit today."}),"\n",(0,s.jsx)(n.p,{children:"It reminds me of an inspiring experience I had in 1981. I was attending a crusade where the Archbishop Benson Idahosa had invited R.W Schambach who was the guest speaker on that particular night. After he had preached and prayed, there were several miracles. The first testifier to come up the platform was a young lady who had been deaf in both ears. R.W Schambach and the Archbishop Benson Idahosa tested her and confirmed she was indeed healed."}),"\n",(0,s.jsx)(n.p,{children:"However, I observed something else that inspired me greatly: the reaction of those two great men of God to the young lady\u2019s testimony. They both jumped and skipped like children, in celebration of that miracle. These were men of God who had seen so many mighty miracles, but it was so inspiring to see them celebrate as though that was the first or only miracle they had ever witnessed."}),"\n",(0,s.jsxs)(n.p,{children:["That\u2019s the way it should be. Be excited always at the seemingly simple things that God does in your life and in the lives of others. Joyfully celebrate His goodness every day. There\u2019s no such thing as a \u201csmall miracle\u201d; every blessing from God is something to shout about, something worth praising Him for. Your heart should always go out to Him in worship, praise and gratitude for His great love, mercies and grace. The Bible says He daily loads us with blessings\u2014benefits: ",(0,s.jsx)(n.em,{children:"\u201cBlessed be the Lord, who daily loadeth us with benefits, even the God of our salvation. Selah\u201d"})," (Psalm 68:19). Hallelujah!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Blessed Father, I thank you for your blessings in my life, every day. I celebrate your life that\u2019s in me, your Word that\u2019s working mightily in me, the prosperity, health, peace, joy, victories and life of glory that I have in Christ. All glory, dominion and praise are yours, now and forever, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Exodus 18:9; Psalm 31:19; Matthew 15:30-31"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Be excited always at the seemingly simple things that God does in your life and in the lives of others. There\u2019s no such thing as a \u201csmall miracle\u201d; every blessing from God is something to shout about."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : Luke 24:13-35; 2 Samuel 9-11",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 2:1-11; Psalm 143-144"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"true-praise-and-worship-may-2nd-2022",children:"TRUE PRAISE AND WORSHIP May 2nd, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-2true-praise-and-worship-b.jpg",alt:"May 2nd, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"For we are the circumcision, which worship God in the spirit, and rejoice in Christ Jesus, and have no confidence in the flesh (Philippians 3:3)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"The Church of Jesus Christ has come of age. Some of the things the Lord tolerated in the past, even though they didn\u2019t make much spiritual sense, are now being corrected. This is because He\u2019s perfecting the Church for the coming of the Lord. For example, true worship and true praise in the house of God: many don\u2019t understand what it is. Some think that when they dance themselves high in church, they\u2019re really praising and worshipping God. But dancing in the flesh is neither praise nor worship."}),"\n",(0,s.jsx)(n.p,{children:"What is praise? And what is worship? Worship is the recognition and appreciation of the reality, blessings, character and greatness of God. This recognition and appreciation, we express in prayer, sacrifice and service. In the New Testament, we offer spiritual sacrifices called praise (Hebrews 13:15, 1 Peter 2:5, Psalm 69:30-31). Hence, praise is an expression of worship. When we praise God, we\u2019re worshipping Him. When you say, \u201cLord, you\u2019re good; you\u2019re kind, gracious and loving; you\u2019ve blessed me, you\u2019re righteous, etc., you\u2019re defining, recognizing, and appreciating His character and His works."}),"\n",(0,s.jsx)(n.p,{children:"Praise is the fruit of our lips\u2014giving thanks to God (Hebrews 13:15). So, in worship or praise, you\u2019re SAYING something to God thankfully. This profuse expression of appreciation of His character and personality in words of thanksgiving should be from the heart; that is the spirit, not the flesh."}),"\n",(0,s.jsx)(n.p,{children:"The dancing that most people do in many churches is in the flesh. That may have its place outside the church. In the New Testament, you don\u2019t dance in the flesh to worship God. Dancing in the flesh is based on the beat of the music. When the beat changes, the dance changes."}),"\n",(0,s.jsx)(n.p,{children:"If you\u2019re inspired to dance, it should be in the spirit and by the Spirit. The dance may not even synchronize or follow the beat of the drums. In such situations, you\u2019ll find yourself jumping, spinning, rejoicing in and from your spirit. We\u2019re the true circumcision who worship God in the Spirit, rejoice in Christ Jesus, and have no confidence in the flesh (Philippians 3:3)."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Gracious heavenly Father, I worship you today for your love, kindness and grace manifested in my life. You\u2019re great and greatly to be praised. I extol your greatness and worship your holy Name, for there\u2019s none like you; your kingdom is forever, your reign is everlasting! You\u2019re the only true and wise God. To you be all glory, honour, majesty, dominion and praise, forever and ever. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Psalm 47:6-7; Psalm 69:30-31; Hebrews 13:15 AMPC"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"What is praise? And what is worship? Worship is the recognition and appreciation of the reality, blessings, character and greatness of God. This recognition and appreciation, we express in prayer, sacrifice and service."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : Luke 24:36-53; 2 Samuel 12-14",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 2:12-16; Psalm 145-146"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"train-yourself-to-pray-may-3rd-2022",children:"TRAIN YOURSELF TO PRAY...... May 3rd, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-3-train-yourself-to-pray.jpg",alt:"May 3rd, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"And it came to pass in those days, that he went out into a mountain to pray, and continued all night in prayer to God (Luke 6:12)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Prayer is a spiritual exercise that you must train for. If you don\u2019t train yourself to pray, you\u2019ll never know how to pray effectively. If all you knew to do was mumble a few words in two minutes and you\u2019re done, it\u2019s time to get some training and discipline."}),"\n",(0,s.jsx)(n.p,{children:"We see the example of the Master in our opening scripture. As long as your body is calling for food, or itching to go do some other things while you ought to be praying, that\u2019s the more reason you must remain there and subject your body to the discipline of the Spirit."}),"\n",(0,s.jsx)(n.p,{children:"However, the purpose of prayer isn\u2019t merely to control or discipline your body, as good as that is. The main purpose of prayer is the conditioning of your spirit, and this is far more important. Prayer brings your spirit to a certain condition that makes your relationship and fellowship with God\u2019s Spirit more real and synchronized with His will and purpose."}),"\n",(0,s.jsx)(n.p,{children:"Then you find that His thoughts become your thoughts and His words become your words. That\u2019s when He puts a word in your mouth which you speak forth and it produces results. It reminds of induced magnetism. If you keep an object beside a magnet long enough, the magnet will induce it to become magnetic itself."}),"\n",(0,s.jsx)(n.p,{children:"It\u2019s the same thing with the Holy Spirit and you; the more you spend time in fellowship with Him, the more His person, glory, character, wisdom and grace are revealed in your walk and in your talk! But if all you do is a few minutes of prayer occasionally, that\u2019s not sufficient time for Him to condition your spirit and have Himself expressed in and through you in a remarkable way."}),"\n",(0,s.jsx)(n.p,{children:"You need to give the Lord time and attention in the place of prayer and become so influenced by Him that you acquire His character and mannerisms. Then you find that your way of thinking and talking (your choice of words) have become so refined by Him. Train yourself for that conditioning to take place. Follow @ PastorChrisLive on KingsChat and join me and millions around the world as we take out time daily to pray\u2026."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for the blessed opportunity to fellowship and commune with you in prayer. I rejoice at the glorious impact and changes that take place in my spirit each time I pray! Thank you for the opportunity to effect positive and lasting changes through prayer, knowing that your ears are ever attentive to my voice to answer my prayers, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 Thessalonians 5:16-17 CEV; Luke 18:1; Ephesians 6:18"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Prayer is a spiritual exercise that you must train for. If you don\u2019t train yourself to pray, you\u2019ll never know how to pray effectively. You need to give the Lord time and attention in the place of prayer."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 1:1-18; 2 Samuel 15-17",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 3:1-9; Psalm 147-148"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"protect-your-heart-may-4th-2022",children:"PROTECT YOUR HEART May 4th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-4-protect-your-heart.jpg",alt:"May 4th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Keep thy heart with all diligence; for out of it are the issues of life (Proverbs 4:23)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"One of the most remarkable things about David was Samuel\u2019s testimony in 1 Samuel 13:14 about his heart; David had a heart that was after God. That\u2019s one of the greatest blessings to ever wish for: having a heart that\u2019s perfect towards God, and a willing mind to do His perfect will. It\u2019s a great blessing from God, but it doesn\u2019t work by itself, because you have a mind and a will of your own. Remember, God gave Saul, David\u2019s predecessor, a good heart (1 Samuel 10:9); yet, he didn\u2019t serve God perfectly."}),"\n",(0,s.jsx)(n.p,{children:"When Saul first met Samuel the Prophet, the Bible says the Spirit of God came upon Saul and he prophesied together with the prophets (1 Samuel 10:10). That was the outward manifestation of what he had received; there was a change on the inside. The Bible specifically says that God gave Saul \u201canother heart.\u201d"}),"\n",(0,s.jsxs)(n.p,{children:["But Saul didn\u2019t maintain his perfect heart. He offered a burnt offering against spiritual instructions (1 Samuel 13:9-11). At another instance, he wouldn\u2019t destroy the Amalekites and everything connected to them as the Lord expressly instructed (1 Samuel 15:1-11). Rather than being remorseful and repentant when he was confronted by Samuel, he was proud. As a result, he was rejected by God from being king over Israel (1 Samuel 15:26), so much so, the Lord said, ",(0,s.jsx)(n.em,{children:"\u201cI regret making Saul king, for he has turned back from following Me and has not performed My commands\u2026\u201d"})," (1 Samuel 15:11 AMPC). What a terrible place to be! Observe the progression."]}),"\n",(0,s.jsx)(n.p,{children:"Saul then became envious of David and continued in his rebellion toward God. His heart went after other things. This is the reason you must protect your heart; keep it pure. Keep it sanctified with God\u2019s Word. Never let anything unwholesome take root in your heart. Pride, anger, malice, bitterness, hatred\u2014these are poisonous to the heart. Completely eschew them from your life and from your heart."}),"\n",(0,s.jsxs)(n.p,{children:["Your thoughts come from your heart; the character of your thoughts reveals the condition of your heart. When you were born again, you were given a heart of love. The Bible says, ",(0,s.jsx)(n.em,{children:"\u201c\u2026the love of God is shed abroad in our hearts by the Holy Ghost which is given unto us\u201d"})," (Romans 5:5). You have a heart of love, a heart that\u2019s filled with God. Protect it; maintain it; keep it the way God wants it."]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, by your Spirit and the Word, I keep my heart sanctified; I banish every evil thought or contrary idea that tries to assault my mind; anger, malice, bitterness, and hatred have no place in my heart. Your love is expressed in my heart today and always, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Proverbs 4:23 Moffat; Philippians 4:8"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Never let anything unwholesome take root in your heart. Pride, anger, malice, bitterness, hatred\u2014these are poisonous to the heart. Completely eschew them from your life and from your heart."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 1:19-51; 2 Samuel 18-19",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 3:10-23; Psalm 149-150"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"speaking-in-tongues-is-a-great-blessing-may-5th-2022",children:"SPEAKING IN TONGUES IS A GREAT BLESSING May 5th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-day-5-speaking-in-tongues-is-a-great-blessing.jpg",alt:"May 5th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"\u2026I will pray with the spirit, and I will pray with the understanding also: I will sing with the spirit, and I will sing with the understanding also (1 Corinthians 14:15)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"There\u2019re Christians who after several years of being born again have never spoken in tongues. They\u2019ve done themselves a great disservice. Some only spoke in tongues on the day they received the Holy Spirit. They often reminisce over that spontaneous experience they once had as though it\u2019s meant to be a one-off experience. But the Scriptures teach that speaking in spiritual tongues is to be a regular part of your prayer life."}),"\n",(0,s.jsx)(n.p,{children:"Speaking in tongues is a great blessing for every Christian, with inexhaustible benefits. Christians who speak in tongues often are hardly ever sick, fearful, or discouraged; they\u2019re on top always, fervent in spirit, serving the Lord."}),"\n",(0,s.jsxs)(n.p,{children:["Having received the Holy Spirit, you\u2019re enabled to speak in tongues of the spirit. The Holy Spirit is the One that gives the utterance to speak in other tongues. On the day of Pentecost, the Bible says, ",(0,s.jsx)(n.em,{children:"\u201cAnd they were all filled with the Holy Ghost, and began to speak with other tongues, as the Spirit gave them utterance\u201d"})," (Acts 2:4)."]}),"\n",(0,s.jsx)(n.p,{children:"Notice it doesn\u2019t say the Holy Spirit spoke with tongues; it was the disciples that spoke with tongues. They didn\u2019t form the words from their heads; they spoke as the Holy Spirit gave them utterance."}),"\n",(0,s.jsxs)(n.p,{children:["The latter part of Acts 2:4 in The Living Bible says the disciples ",(0,s.jsx)(n.em,{children:"\u201c...began speaking in languages they didn\u2019t know, for the Holy Spirit gave them this ability.\u201d"})," The Good News Bible says ",(0,s.jsx)(n.em,{children:"\u201cThey were all filled with the Holy Spirit and began to talk in other languages, as the Spirit enabled them to speak.\u201d"})]}),"\n",(0,s.jsx)(n.p,{children:"Some say they\u2019re waiting for the Holy Spirit to take over their lips before they speak in tongues; that\u2019s not how it works. You can speak in tongues anytime, anywhere, and for as long as you choose. The Holy Spirit already gave you the ability; therefore, go ahead and speak in tongues even now, as an act of your faith in the Father\u2019s fulfilled promise in Christ."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"Dear Father, what a blessing it is to build up myself, like an edifice, as I pray in tongues. Even now, I\u2019m charged up like a battery, formidable, able to withstand anything and win always. I experience divine success, dominion and victory, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 Corinthians 14:14-15; Joel 2:28; Jude 1:20"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Speaking in tongues is a great blessing for every Christian, with inexhaustible benefits. Christians who speak in tongues often are hardly ever sick, fearful, or discouraged; they\u2019re on top always, fervent in spirit, serving the Lord."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 2:1-25; 2 Samuel 20-21",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 4:1-10; Proverbs 1"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"his-love-manifested-in-you-may-6th-2022",children:"HIS LOVE MANIFESTED IN YOU May 6th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-6-his-love-manifested-in-you.jpg",alt:"May 6th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"For we which live are alway delivered unto death for Jesus\u2019 sake, that the life also of Jesus might be made manifest in our mortal flesh (2 Corinthians 4:11)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"The proof that we know God\u2019s love is how we share it. Did you ever stop to think about that? We don\u2019t know God\u2019s love until we share it and manifest it. Jesus was the manifestation of God\u2019s love. He was God\u2019s love in the flesh. That\u2019s to say He was the \u201cbody\u201d of love."}),"\n",(0,s.jsx)(n.p,{children:"The gospels: Matthew, Mark, Luke and John show Him as the embodiment of love. Jesus didn\u2019t merely have love; He was love made flesh! That\u2019s one of the most extraordinary truths ever revealed. Jesus is the perfect description of love."}),"\n",(0,s.jsx)(n.p,{children:"We read in the Bible that God is love (1 John 4:8), but this will never mean anything to you until you get to know Jesus. Many think of God with various imaginations. But Jesus said, \u201cIf you\u2019ve seen me, you\u2019ve seen the Father\u201d (John 14:9). Hallelujah! The Bible says as He is, so are we in this world (1 John 4:17). God\u2019s plan is to reveal Himself\u2014character, ability, and His nature of love and righteousness through each one of us."}),"\n",(0,s.jsx)(n.p,{children:"The expression of His love in and through you is a key indicator of spiritual growth. You\u2019re truly spiritually mature when the love of God is expressed more and more in your words, thoughts, attitude and actions. Walking in love is the manifestation of the life and nature of Christ that\u2019s in your spirit. Praise God!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"The Love of God is shed abroad in my heart by the Holy Ghost. Love is the manifestation of the life of Christ in me. I\u2019m growing in the love of God, manifesting it everywhere more and more in my words, thoughts, attitudes and actions. Hallelujah!"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 Peter 2:9 AMPC; Romans 5:5; 1 John 4:16"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"The proof that we know God\u2019s love is how we share it. Did you ever stop to think about that? We don\u2019t know God\u2019s love until we share it and manifest it."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 3:1-21; 2 Samuel 22",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 4:11-21; Proverbs 2"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"soundness-of-health-may-7th-2022",children:"SOUNDNESS OF HEALTH May 7th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-7-soundness-of-health.jpg",alt:"May 7th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Beloved, I wish above all things that thou mayest prosper and be in health, even as thy soul prospereth (3 John 1:2)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"One of the areas Satan likes to take advantage of God\u2019s people is in their health. Thus, in keeping yourself in health, the first thing you need to consciously recognize is that the Word of God is pure medicine. Meditating on the Word will keep you in soundness of health\u2014spirit, soul and body."}),"\n",(0,s.jsx)(n.p,{children:"Every day, meditate on the Word. Don\u2019t wait for trouble to strike before you start \u201ctrying\u201d to meditate on the Word. Meditate on the Word ahead of the evil day."}),"\n",(0,s.jsx)(n.p,{children:"Something else you need that\u2019s of equal importance is the guidance of the Holy Spirit. Be sensitive to the Holy Spirit about what you eat and how you treat your body. Such things aren\u2019t too trivial for Him to give you clear guidance about. Trust Him; He\u2019ll teach you how to be wise about food."}),"\n",(0,s.jsxs)(n.p,{children:["It\u2019s God\u2019s will and desire for you to be healthy and strong. That\u2019s why, besides teaching and guiding you on the things to do to keep your body in health, He also \u201cperambulates\u201d in you to ward off sickness, disease and infirmities from your body: ",(0,s.jsxs)(n.em,{children:["\u201cAnd what agreement hath the temple of God with idols? for ye are the temple of the living God; as God hath said, I will dwell in them, and ",(0,s.jsx)(n.strong,{children:"walk"})]}),"(perambulate) ",(0,s.jsx)(n.em,{children:"in them; and I will be their God, and they shall be my people\u201d"})," (2 Corinthians 6:16). Praise God!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, your Word is medicine for my body and health to every fibre of my being. I\u2019m perpetually healthy and strong. And now, I pray for your children around the world who are sick in their bodies, that your healing power will surge through them now, healing their bodies and getting rid of the flu, the migraine, the cancer or any infirmity in their bodies, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Proverbs 4:20-22; Isaiah 58:8; Romans 8:10-11 ESV"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"In keeping yourself in health, the first thing you need to consciously recognize is that the Word of God is pure medicine. Meditating on the Word will keep you in soundness of health\u2014spirit, soul and body."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 3:22-36; 2 Samuel 23-24",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 5:1-13; Proverbs 3"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"practise-being-alone-with-god-may-8th-2022",children:"PRACTISE BEING ALONE WITH GOD May 8th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-8-practise-being-alone-with-god-b.jpg",alt:"May 8th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"But Jesus often withdrew to the wilderness for prayer (Luke 5:16 NLT)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"There\u2019s something about solitude. If you don\u2019t understand the value of solitude, there\u2019s a lot that you can miss in life. It\u2019s so important to have personal times alone with God. Before revelation comes isolation. When you learn the value of having private times of fellowship with the Lord, you\u2019ll be amazed at the transformation that\u2019ll take place in your life and how effective you\u2019d be in the things of God."}),"\n",(0,s.jsx)(n.p,{children:"Never take your private times with the Lord lightly. Maybe you have some people that every now and then are at your door because they need to see you or speak with you about one thing or the other. Be smart. If you spend most of your time in casual or vain chatter with others and spend lesser time fellowshipping with the Spirit, you\u2019re not going to accomplish much in life."}),"\n",(0,s.jsxs)(n.p,{children:["Jesus understood the value and power of privacy with God. In Matthew 14:23, the Bible says, ",(0,s.jsx)(n.em,{children:"\u201cAnd when he had sent the multitudes away, he went up into a mountain apart to pray: and when the evening was come, he was there alone.\u201d"})," Our theme verse shows us that He did this often: ",(0,s.jsx)(n.em,{children:"\u201cBut Jesus often withdrew to the wilderness for prayer\u201d"})," (Luke 5:16 NLT). At the time everyone was clamouring for His attention, He withdrew into the wilderness to pray."]}),"\n",(0,s.jsxs)(n.p,{children:["In fact, on a certain occasion, He \u201cescaped\u201d to go and pray, and they were looking for Him. The Bible says, ",(0,s.jsx)(n.em,{children:"\u201cAnd in the morning, rising up a great while before day, he went out, and departed into a solitary place, and there prayed. And Simon and they that were with him followed after him\u201d"})," (Mark 1:35-36). Practise this. Make the effort to have your personal times with God every day."]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, as I spend time with you in fellowship, the intensity of your glory in my life is increased; I\u2019m transfigured from glory to glory. I experience victory, divine health and blessings! My life is an expression of your supernatural grace, glory, perfection, beauty and righteousness, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Ephesians 6:18; Psalm 1:1-3; Matthew 6:6"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"There\u2019s something about solitude. If you don\u2019t understand the value of solitude, there\u2019s a lot that you can miss in life. It\u2019s so important to have personal times alone with God."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 4:1-26; 1 Kings 1",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 6:1-11; Proverbs 4"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"the-power-is-released-when-you-speak-may-9th-2022",children:"THE POWER IS RELEASED WHEN YOU SPEAK May 9th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-9-the-power-is-released-when-you-speak.jpg",alt:"May 9th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"For with the heart man believeth unto righteousness; and with the mouth confession is made unto salvation (Romans 10:10)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Perhaps you\u2019re often described as a cool and calm person, very quiet. But when it comes to speaking or releasing the Word\u2014using it against the adversary and adversities\u2014be wild. Refuse to be cool and calm. It brings to mind the third of the three levels of meditation that the Bible talks about: \u201croaring!\u201d The Hebrew word is \u201cHagar.\u201d"}),"\n",(0,s.jsx)(n.p,{children:"Hagar means roaring or shouting the Word. In putting the Word of God to work, be on the offensive. When sickness tries to attack you or your loved ones, get wild in your affirmation of the Word. Don\u2019t stop until the Word you speak prevails over that situation, because it\u2019ll surely prevail!"}),"\n",(0,s.jsxs)(n.p,{children:["The Bible says, ",(0,s.jsx)(n.em,{children:"\u201cSo mightily grew the Word of God and prevailed\u201d"})," (Acts 19:20). As you speak the Word, it\u2019ll keep growing; the necessary result, therefore, will be for it to prevail. So, use the Word to deal with any situation, irrespective of how dire or grave the situation is."]}),"\n",(0,s.jsx)(n.p,{children:"Every so often, declare, \u201cI\u2019m born again; I have the life of God in me. I refuse to accommodate sickness or infirmities in my body. My body is the temple of the Holy Spirit; therefore, it\u2019s perfected in Christ Jesus. I live in health.\u201d Talk like this every day, because the power is released when you speak."}),"\n",(0,s.jsx)(n.p,{children:"The power doesn\u2019t work just because you know it or believe it. The principle is that you must SAY it for it to work. That\u2019s what we read in our theme verse. Your mouth isn\u2019t just for communicating and eating; those are the lesser functions of the mouth. The primary function of your mouth is to frame your life aright."}),"\n",(0,s.jsxs)(n.p,{children:["The Prophet said, ",(0,s.jsx)(n.em,{children:"\u201cTake with you words...\u201d"})," (Hosea 14:2); not just any words but God\u2019s Word. The Word of God in your mouth is a creative force; use it to change circumstances and make your life excellent, from glory to glory. Hallelujah!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, your Word dwells in me richly through meditation, and I speak it forth boldly. My words are creative and divine, infused with power to change circumstances; they\u2019re empowered to produce life, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Joshua 1:8; Mark 11:23"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"The power doesn\u2019t work just because you know it or believe it. The principle is that you must SAY it for it to work. The primary function of your mouth is to frame your life aright."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 4:27-54; 1 Kings 2-3",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 6:12-20; Proverbs 5"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"power-of-a-strong-desire-may-10th-2022",children:"POWER OF A STRONG DESIRE May 10th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-10-power-of-a-strong-desire.jpg",alt:"May 10th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"And ye shall seek me, and find me, when ye shall search for me with all your heart. And I will be found of you, saith the LORD\u2026 (Jeremiah 29:13-14)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"God never ignores a strong desire-a strong desire to know Him, walk with Him and do His will. That strong desire births an unquenchable urge for prayer. That\u2019s what we mean sometimes when we say someone is taken over by the \u201cspirit of prayer.\u201d It means the Holy Spirit has brought an overwhelming atmosphere and influence of prayer on your heart."}),"\n",(0,s.jsx)(n.p,{children:"When that happens, prayer becomes all that you do and think about. That influence could stay on you for a protracted period of time. However, this isn\u2019t just something that happens when God chooses to; rather, it\u2019s what you invite by desire. The Bible says He satisfies the thirsty soul (Psalm 107:9)."}),"\n",(0,s.jsx)(n.p,{children:"When you begin that fellowship of prayer, it won\u2019t be long before that spirit of intercession comes on you and you find that you\u2019re constantly praying. Every now and then the Spirit will pull you to His motivations, visions, plans and purposes, the things that He cares about and wants to do. He\u2019d be expressing Himself through you in prayer."}),"\n",(0,s.jsxs)(n.p,{children:["This is something that should be evident in the life of every Christian; a strong desire for the Lord, His Word, and His influence in your life. When you have it, you can easily influence those around you with it. So, cultivate it. Stir your heart to have a strong desire and maintain your zeal, love and passion for the Lord at all times. David, inspired by a strong desire for the Lord, said, ",(0,s.jsx)(n.em,{children:"\u201cO God, thou art my God; early will I seek thee: my soul thirsteth for thee, my flesh longeth for thee in a dry and thirsty land, where no water is\u201d"})," (Psalm 63:1)."]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, you\u2019re my glory, my joy and my all; my soul pants for you much more than the deer pants after the water brooks. Thank you for your glory in my life. My desire is to know you more and fulfill your perfect will for me, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Luke 18:1-8; Jeremiah 29:13-14 AMPC"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"God never ignores a strong desire-a strong desire to know Him, walk with Him and do His will. This is something that should be evident in the life of every Christian."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 5:1-30; 1 Kings 4-6",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 7:1-9; Proverbs 6"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"prove-your-love-by-winning-souls-may-11th-2022",children:"PROVE YOUR LOVE BY WINNING SOULS May 11th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-11-prove-your-love-by-winning-souls-b.jpg",alt:"May 11th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"To wit, that God was in Christ, reconciling the world unto himself, not imputing their trespasses unto them; and hath committed unto us the word of reconciliation (2 Corinthians 5:19)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Our evangelical responsibility is something that all of heaven takes seriously. Sadly, many don\u2019t understand how serious this really is. There\u2019re Christians who are still asking if whether or not someone who doesn\u2019t win souls will go to heaven. The question to ask such people is, \u201cIf someone doesn\u2019t love God, or love Jesus, will that person go to heaven?\u201d That\u2019s not going to happen."}),"\n",(0,s.jsxs)(n.p,{children:["The Bible says, ",(0,s.jsx)(n.em,{children:"\u201c\u2026every one that loveth him that begat loveth him also that is begotten of him\u201d"})," (1 John 5:1). If you love the Father, you must love Jesus. 1 Corinthians 16:22 says, ",(0,s.jsx)(n.em,{children:"\u201cIf any man love not the Lord Jesus Christ, let him be Anathema Maranatha.\u201d"})," That means let him be accursed."]}),"\n",(0,s.jsxs)(n.p,{children:["The proof of your love for Jesus is keeping His commandments\u2014His Word (John 14:15). His Word says, ",(0,s.jsx)(n.em,{children:"\u201c...Go ye into all the world and preach the Gospel to every creature\u201d"})," (Mark 16:15). So you keep that word by being a soul winner. Someone might ask, \u201cHow about those who lived before Jesus came and didn\u2019t quite have the opportunity to express their love for Him?\u201d They had the Word of God\u2014His laws and commandments."]}),"\n",(0,s.jsxs)(n.p,{children:["Job said, ",(0,s.jsx)(n.em,{children:"\u201c\u2026I have esteemed the words of his mouth more than my necessary food\u201d"})," (Job 23:12). Jesus is the Word (John 1:14). Hallelujah! Thus, every one of them had a clear opportunity to express their love for Him. He said in Matthew 28:19, ",(0,s.jsx)(n.em,{children:"\u201cGo ye therefore, and teach all nations\u2026.\u201d"})," \xa0That means make pupils of those in your world, teach them about God and the Kingdom-life, establish His Kingdom in the earth. Your love and commitment to the Lord must reflect in your dedication to the preaching of the Gospel."]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"I love you Lord, and I boldly and openly proclaim the mysteries of the Gospel as an effective witness, and impact my world with your righteousness. Thank you for empowering me by your Spirit to be an effective witness, transforming my world and beyond with the news of your saving power, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"John 14:15; Matthew 28:19-20; 2 Corinthians 5:14"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"The proof of your love for Jesus is keeping His commandments\u2014His Word. Your love and commitment to the Lord must reflect in your dedication to the preaching of the Gospel."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 5:31-47; 1 Kings 7-8",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 7:10-24; Proverbs 7"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"christianity-and-the-resurrection-may-12th-2022",children:"CHRISTIANITY AND THE RESURRECTION May 12th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-12-christianity-and-the-resurrection.jpg",alt:"May 12th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Now if Christ be preached that he rose from the dead, how say some among you that there is no resurrection of the dead? (1 Corinthians 15:12)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Sometime ago, a certain well-known \u201cpriest\u201d publicly stated that he used to believe in the resurrection of the Lord Jesus, but he now had reasons to question whether or not Jesus truly rose from the dead. The fact of the matter is, he never believed in Jesus even though he claimed to be a Christian. It\u2019s impossible to be a Christian without believing absolutely in the resurrection of Jesus!"}),"\n",(0,s.jsxs)(n.p,{children:["There\u2019s no Christianity without the resurrection. Christianity only begins from the resurrection, not the Cross. The death of Jesus didn\u2019t provide eternal life; it\u2019s the resurrection of Jesus Christ that ushered us into eternal life. The Apostle Paul explains it in Romans 6:4; he said, ",(0,s.jsx)(n.em,{children:"\u201cTherefore we are buried with him by baptism into death: that like as Christ was raised up from the dead by the glory of the Father, even so we also should walk in newness of life.\u201d"})]}),"\n",(0,s.jsx)(n.p,{children:"To not believe in the resurrection is to deny the possibility and availability of eternal life. And if you don\u2019t have eternal life, you\u2019re not a Christian. A Christian is someone who has received eternal life. What is eternal life? It\u2019s the life and nature of God\u2014the quality of the divine essence."}),"\n",(0,s.jsx)(n.p,{children:"For example, dogs aren\u2019t goats, chickens aren\u2019t ostriches; no matter the resemblance between them, they don\u2019t have the same life and they\u2019ll never produce or reproduce after the other. Dogs will never reproduce goats and vice versa. The Christian is literally born of God, with the God-life. It was the resurrection of Jesus Christ that made that possible. Hallelujah!"}),"\n",(0,s.jsxs)(n.p,{children:["More so, the Bible says Jesus was proven or declared to be God in human flesh according to the resurrection from the dead (Romans 1:4). Then the Spirit of God, through the Apostle Paul, brought the doctrine to a climax in 1 Corinthians 15:12-19; study the entire reference. The 19th verse says, ",(0,s.jsx)(n.em,{children:"\u201cIf in this life only we have hope in Christ, we are of all men most miserable.\u201d"})," Thank God for the resurrection! It\u2019s what gave us life. Blessed be God!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for the glorious resurrection of Christ that ushered in Christianity and brought us into eternal life\u2014the indestructible, uninfectable, and impregnable life. I walk in the newness of life, conscious that I\u2019ve been raised together with Christ and made to sit together with Him in the place of victory, authority and dominion forever, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Galatians 2:20; Romans 6:4; 1 Corinthians 15:12-20"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"There\u2019s no Christianity without the resurrection. Christianity only begins from the resurrection, not the Cross. The death of Jesus didn\u2019t provide eternal life; it\u2019s the resurrection of Jesus Christ that ushered us into eternal life."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 6:1-24;1 Kings 9",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 7:25-40; Proverbs 8"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"we-dont-live-by-blood-may-13th-2022",children:"WE DON\u2019T LIVE BY BLOOD May 13th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-13-we-dont-live-by-blood.jpg",alt:"May 13th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"But if the Spirit of him that raised up Jesus from the dead dwell in you, he that raised up Christ from the dead shall also quicken your mortal bodies by his Spirit that dwelleth in you (Romans 8:11)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["Leviticus 17:11 says, ",(0,s.jsx)(n.em,{children:"\u201cFor the life of the flesh is in the blood\u2026.\u201d"})," \xa0Before you were born again, your body lived by blood. But from the day you were born again and received the Holy Spirit, you received the God-life that doesn\u2019t require blood for its sustenance. Now, you can better appreciate what the Bible says in John 1:12-13: ",(0,s.jsx)(n.em,{children:"\u201cBut as many as received him, to them gave he power to become the sons of God\u2026 Which were born, not of blood, nor of the will of the flesh, nor of the will of man, but of God.\u201d"})]}),"\n",(0,s.jsxs)(n.p,{children:["When you\u2019re born of God, you\u2019re not human; you\u2019re divine. That old system of having life in your body through your blood ceased to be; it was replaced by the life of the Holy Spirit. No wonder Jesus said in Mark 16:18, ",(0,s.jsx)(n.em,{children:"\u201c\u2026if they drink any deadly thing, it shall not hurt them\u2026.\u201d"})]}),"\n",(0,s.jsx)(n.p,{children:"There\u2019s no poison that gets into your system through the blood vessels that\u2019s potent enough to wreak a havoc. This is because the life in you is indestructible\u2014it\u2019s the life of the Holy Spirit! Those who don\u2019t understand this truth live the ordinary human life. That\u2019s the reason they can be buffeted and affected by sickness, disease, infirmities, failure, lack and the corrupting influences of this present world of darkness."}),"\n",(0,s.jsx)(n.p,{children:"Refuse to think, talk, and act or live like a mere human being; otherwise, you\u2019ll suffer what humans suffer. Don\u2019t let the lamentation of the Psalmist in Psalm 82:5-7 describe the reality of your life. It says*, \u201cThey know not, neither will they understand; they walk on in darkness: all the foundations of the earth are out of course. I have said, Ye are gods; and all of you are children of the most High. But ye shall die like men, and fall like one of the princes.\u201d* Rather, live the God-life, the divine life, because you\u2019re born of God, which makes you a god. Gods don\u2019t live by blood! Hallelujah!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"I live above this world and its systems, above sickness, disease and the corrupting forces of this present world of darkness. The power of the Spirit that raised Christ from the dead has vitalized and given life to my physical body. I live the transcendent life of glory in Christ Jesus. Amen!"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Luke 10:19; Romans 8:10-11 TPT"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Before you were born again, your body lived by blood. But from the day you were born again and received the Holy Spirit, you received the God-life that doesn\u2019t require blood for its sustenance."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 6:25-59; 1 Kings 10-11",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 8:1-13; Proverbs 9"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"to-know-him-is-to-love-him-may-14th-2022",children:"TO KNOW HIM IS TO LOVE HIM May 14th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-14-to-know-him-is-to-love-him.jpg",alt:"May 14th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"That I may know him, and the power of his resurrection, and the fellowship of his sufferings, being made conformable unto his death (Philippians 3:10)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["Notice what the Apostle Paul said in the opening verse: ",(0,s.jsx)(n.em,{children:"\u201cThat I may know him\u2026.\u201d"})," This was his dream, because to know Him (Jesus) is to love Him. Anyone who doesn\u2019t love Jesus doesn\u2019t know Him. If they ever get to know who He is, they\u2019ll most certainly fall in love with Him. The more you know Jesus, the more you love Him. Hallelujah."]}),"\n",(0,s.jsxs)(n.p,{children:["It brings to mind the story of Paul before his conversion. He was a Pharisee and a member of the critic\u2014religious sect that spearheaded the persecution of Christians in the early Church. Authorized by the government of the day, the Bible says, ",(0,s.jsx)(n.em,{children:"\u201c...he made havock of the church, entering into every house, and haling men and women committed them to prison\u201d"})," (Acts 8:3)."]}),"\n",(0,s.jsxs)(n.p,{children:["He didn\u2019t stop at that. Luke tells us how he continued to issue violent threats of murder against the followers of the Lord (Acts 9:1). But something happened that changed his life forever. He had a destiny-shaping encounter with the Lord on the road to Damascus. The Bible says, ",(0,s.jsx)(n.em,{children:"\u201cAnd as he journeyed, he came near Damascus: and suddenly there shined round about him a light from heaven: And he fell to the earth, and heard a voice saying unto him, Saul, Saul, why persecutest thou me? And he said, Who art thou, Lord? And the Lord said, I am Jesus whom thou persecutest: it is hard for thee to kick against the pricks. And he trembling and astonished said, Lord, what wilt thou have me to do\u2026\u201d"})," (Acts 9:3-6). Hallelujah!"]}),"\n",(0,s.jsx)(n.p,{children:"Paul, as a result of this new knowledge of the Lord, couldn\u2019t keep back anymore; everywhere he went, he testified that Jesus is alive and that He\u2019s real! Paul\u2019s love for Jesus was unquenchable because he now knew the Lord."}),"\n",(0,s.jsxs)(n.p,{children:["No wonder he said in Ephesians 3:19, ",(0,s.jsx)(n.em,{children:"\u201cAnd to know the love of Christ\u2026that ye might be filled with all the fullness of God.\u201d"})," This is God\u2019s will for you: that you\u2019d have a revelation of Christ beyond human understanding and be filled with His fullness, overflowing in His love, manifesting His life, nature, character and power. Hallelujah!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for the revelation of your unfathomable, unconditional, pure and eternal love in my spirit. I\u2019m rooted and grounded in your love, and I manifest and radiate your love to my world today, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Ephesians 3:17-19; Philippians 3:7-10"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Anyone who doesn\u2019t love Jesus doesn\u2019t know Him. If they ever get to know who He is, they\u2019ll most certainly fall in love with Him. The more you know Jesus, the more you love Him. Hallelujah."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 6:60-71; 1 Kings 12-14",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 9:1-10; Proverbs 10"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"the-supply-zone-may-15th-2022",children:"THE SUPPLY ZONE May 15th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-15-the-supply-zone.jpg",alt:"May 15th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"According as his divine power hath given unto us all things that pertain unto life and godliness, through the knowledge of him that hath called us to glory and virtue (2 Peter 1:3)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Blessed be God! The Word of God is so enthralling, edifying and fulfilling! Think about what we just read in our theme verse: we\u2019ve been given everything we require for an excellent life of glory, dominion, righteousness and superabundance."}),"\n",(0,s.jsxs)(n.p,{children:["It reminds of what the Spirit says through Paul in Ephesians 1:3: ",(0,s.jsx)(n.em,{children:"\u201cBlessed be the God and Father of our Lord Jesus Christ, who hath blessed us with all spiritual blessings in heavenly places in Christ.\u201d"})," As a Christian, you\u2019re in the supply zone; you dwell in surplus."]}),"\n",(0,s.jsxs)(n.p,{children:["David said, ",(0,s.jsx)(n.em,{children:"\u201cHe maketh me to lie down in green pastures\u2026\u201d"})," (Psalm 23:2); that\u2019s your abode\u2014the place of never -ending, superabundant wealth, where you have no consciousness of want or need."]}),"\n",(0,s.jsx)(n.p,{children:"You need to know who you are as a child of God: you\u2019re the seed of Abraham. You\u2019re an heir of God and a joint heir with Christ. That means you own this world. Everything the Father owns belongs to you, because you\u2019re His heir. How then could you be broke? Think about the monarchs of this world; their children are born into wealth. Do you think it\u2019s any different with our God? He\u2019s the Monarch of the universe."}),"\n",(0,s.jsx)(n.p,{children:"No wonder the Spirit prayed through the Apostle Paul in Ephesians 1:18 that the eyes of your understanding would be enlightened to know the hope of His calling, and the unfathomable riches of the glory of His inheritance bequeathed to His saints. You were born into an incorruptible, inexhaustible inheritance. Become conscious of this every day and it\u2019ll change your life completely."}),"\n",(0,s.jsx)(n.p,{children:"Christianity is a life you practise. As long as you remain in that babyhood stage of always needing and expecting some kind of financial miracles, you\u2019ve not yet understood Christianity and your inheritance in Christ. What you need is a \u201cmental crossover\u201d into the zone of continual abundant supply."}),"\n",(0,s.jsx)(n.p,{children:"Study 2 Corinthians 9:8 AMPC; it says*, \u201cAnd God is able to make all grace (every favor and earthly blessing) come to you in abundance, so that you may always and under all circumstances and whatever the need be self-sufficient [possessing enough to require no aid or support and furnished in abundance for every good work and charitable donation].\u201d* This is where God wants you to function; it\u2019s the life Christ has given us!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"God has made all grace, every favour and earthly blessing come to me in abundance; therefore, I\u2019m self-sufficient. I was born into an incorruptible, inexhaustible inheritance. Christ is in me, and He\u2019s my all! With Him, I have all things. Blessed be God!"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Philippians 4:19; Psalm 23:1-2; Psalm 68:19; 1 Corinthians 3:21"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"You need to know who you are as a child of God: you\u2019re the seed of Abraham. You\u2019re an heir of God and a joint heir with Christ. That means you own this world."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 7:1-24; 1 Kings 15-17",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 9:11-19; Proverbs 11"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"mandate-to-shine-may-16th-2022",children:"MANDATE TO SHINE May 16th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-16-mandate-to-shine.jpg",alt:"May 16th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Ye are the light of the world. A city that is set on an hill cannot be hid. Let your light so shine before men, that they may see your good works, and glorify your Father which is in heaven (Matthew 5:14,16)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"It\u2019s estimated that there\u2019re almost 9 billion people in the world today. Despite the statistics, it\u2019s interesting to know that no other human was exactly like you in the past, none is like you now, and none will ever be like you. God made it so! Your uniqueness is such that can\u2019t be obscured. It\u2019s the reason you must live your best, be your best, and put your best into serving the Lord, Jesus Christ."}),"\n",(0,s.jsx)(n.p,{children:"In our theme scripture, Jesus said, \u201cYou\u2019re the light of the world, a city on a hill that can\u2019t be hidden.\u201d But He didn\u2019t stop there; He added, \u201cLet your light so shine before men, that they may see your good works, and glorify your Father which is in heaven.\u201d This is significant; it lets you know you have a divine mandate to shine before all men\u2013anywhere and everywhere."}),"\n",(0,s.jsx)(n.p,{children:"This means you can always win and make progress, no matter the circumstances of your life. Joseph, the son of Jacob, is a notable example in this regard. He was sold into slavery by his brothers and later put in prison after being wrongly accused. Yet, he shone so brightly in the midst of all those difficult times and eventually went on to become the prime minister in a foreign country, according to God\u2019s plan and purpose."}),"\n",(0,s.jsx)(n.p,{children:"As you heed the Master\u2019s instruction to shine, setting forth His wonderful deeds and displaying His virtues and perfections, the Scripture says men would glorify your Father in heaven. I encourage you to put your focus and best energies on what God has called you to do, no matter where you are in life. It won\u2019t be long before you\u2019re catapulted to a higher dimension of success, with your life continually bringing Him glory and honour."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"I\u2019m a partaker of the inheritance of the saints in the kingdom of light. Darkness has no place in me, because I\u2019m native-born to the light and I live continually in the light of God\u2019s Word. I manifest this light to my world ever so brightly today, as I give expression to the life of Christ and His nature of righteousness in me, functioning with excellence, precision and wisdom in all I do. Glory to God!"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 Peter 2:9 AMPC; Isaiah 60:1-3"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"None is like you now, and none will ever be like you. It\u2019s the reason you must live your best, be your best, and put your best into serving the Lord, Jesus Christ."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 7:25-53; John 8:1-11; 1 Kings 18-19",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 9:20-27; Proverbs 12"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"transformation-by-the-word-may-17th-2022",children:"TRANSFORMATION BY THE WORD May 17th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-17-transformation-by-the-word.jpg",alt:"May 17th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"But we all, with open face beholding as in a glass the glory of the Lord, are changed into the same image from glory to glory, even as by the Spirit of the Lord (2 Corinthians 3:18)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"As you study the Word of God, you see a glorious you, because the Word mirrors you. The picture of you that you see in the Word is always one of glory. You\u2019ll see that divinity is at work in you; you\u2019ll see the power of an endless life. The more you see it, the more you\u2019re transformed into the same image that you see in the Word. It\u2019s an amazing spiritual principle. That\u2019s the reality of how the Word of God works."}),"\n",(0,s.jsx)(n.p,{children:"That\u2019s why James admonishes us to keep looking into the Word of God, which he calls the perfect law of liberty (James 1:25). Keep looking at the scriptures that talk about your righteousness, your oneness with the Lord, your triumphant life in Christ Jesus."}),"\n",(0,s.jsx)(n.p,{children:"Jesus said in John 15:16*, \u201c...I have chosen you, and ordained you, that ye should go and bring forth fruit, and that your fruit should remain....\u201d* This means He\u2019s given you a life of endless prosperity; your success shouldn\u2019t be short-lived. Contemplate this, every day. As you behold such words, the reality of what they say becomes your vital experience."}),"\n",(0,s.jsx)(n.p,{children:"Through the Word, you\u2019re established in righteousness and flourish like the palm tree, with great grace and glory! Maybe it\u2019s your health; Isaiah 33:24 says \u201cthey that dwell in Zion shall not say, \u2018I am sick.\u2019\u201d Meditate on it consciously. As you do, you\u2019d come to the realization that the reason you don\u2019t say, \u201cI\u2019m sick\u201d is that you don\u2019t get sick. You\u2019re an associate of the God-kind; you have the indestructible life of God in you."}),"\n",(0,s.jsx)(n.p,{children:"The same goes for your safety and protection. The Word says in Psalm 23:4*, \u201cYea, though I walk through the valley of the shadow of death, I will fear no evil: for thou art with me; thy rod and thy staff they comfort me.\u201d* That means no matter what happens, your safety is guaranteed in Christ. Christ is the environment in which you dwell; therefore, you\u2019re forever in safety. Take this seriously. Outline areas where you want changes; search out the relevant scriptures and meditate on them consciously. Praise God!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for your glory in my life. As I constantly behold the glory in your Word for my health, finances, family and safety, I\u2019m transformed into the same image that I see, from glory to glory, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Acts 20:32; Romans 12:2"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"As you study the Word of God, you see a glorious you, because the Word mirrors you. The picture of you that you see in the Word is always one of glory. The more you see it, the more you\u2019re transformed."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 8:12-30; 1 Kings 20-21",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 10:1-13; Proverbs 13"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"lets-give-him-what-he-wants-may-18th-2022",children:"LET\u2019S GIVE HIM WHAT HE WANTS May 18th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-18-lets-give-him-what-he-wants-b.jpg",alt:"May 18th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Do not conform to the pattern of this world, but be transformed by the renewing of your mind. Then you will be able to test and approve what God\u2019s will is\u2014his good, pleasing and perfect will (Romans 12:2 NIV)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Right from the very beginning of my Christian walk, I always wanted to know what God wanted, what\u2019s on His mind. I wanted to know His perfect will about everything, so I could serve Him rightly. I didn\u2019t want to ever find myself in a certain direction, or doing something, only to discover that it wasn\u2019t what God wanted."}),"\n",(0,s.jsx)(n.p,{children:"So, oftentimes, He directed me to the Scriptures. But He did something even more: He opened my understanding in an extraordinary way, because He saw my heart. It\u2019s one of the ways I get to know His heart about anything, about what He wants."}),"\n",(0,s.jsx)(n.p,{children:"Therefore, in serving God, it\u2019s about Him, what He wants and how He wants it. It\u2019s not about our feelings, impulses or sense knowledge. For example, in praising and worshipping Him, we shouldn\u2019t find ourselves doing it in a way that He isn\u2019t pleased with. Praise and worship in the Church is so important to God; it\u2019s something we must do according to the prescribed order\u2014according to His Word."}),"\n",(0,s.jsxs)(n.p,{children:["Some believe clapping is a way to praise and worship God, but it isn\u2019t. Clapping is applause; you don\u2019t applaud God. These are some of the things we\u2019ve done in the past that God wants us to correct. He is perfecting His Church for His return. We don\u2019t clap for God. You may say, \u201cBut Psalm 47:1 says*, \u2018O clap your hands, all ye people; shout unto God with the voice of triumph.\u201d*\u2019 That was written for those in the Old Testament and it wasn\u2019t for us to copy because those in the Old Testament mostly walked in the flesh. If you want to know how you\u2019re supposed to serve God, how you\u2019re to praise and worship Him as a new creation, study the New Testament, the epistles. There, we\u2019re told ",(0,s.jsx)(n.em,{children:"\u201cFor we are the circumcision, who worship God in the Spirit, rejoice in Christ Jesus, and have no confidence in the flesh\u201d"})," (Philippians 3:3 NKJV)."]}),"\n",(0,s.jsx)(n.p,{children:"The Lord is perfecting His Church with His truth. So, we\u2019ve got to put things in their proper places so we\u2019ll get the right results. Let\u2019s give Him what He wants, which is true spiritual worship through the Holy Spirit."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for the ministry of your Spirit through which you\u2019re perfecting the Church and helping us to place a premium on living by your Word, rendering to you worship in spirit and in truth. Your Word is gaining the pre-eminence, with true worship taking centre stage among your congregations as your glory fills our hearts, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Matthew 15:8-9 NIV; Acts 17:30; John 4:23"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"The Lord is perfecting His Church with His truth. So, we\u2019ve got to put things in their proper places so we\u2019ll get the right results. Let\u2019s give Him what He wants, which is true spiritual worship through the Holy Spirit."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 8:31-47; 1 Kings 22",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 10:14-21; Proverbs 14"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"protect-your-heart-with-gods-truth-may-19th-2022",children:"PROTECT YOUR HEART WITH GOD\u2019S TRUTH May 19th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-19-protect-your-heart-with-gods-truth.jpg",alt:"May 19th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"My child, listen and be wise. Keep your heart on the right course (Proverbs 23:19 NLT)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"There\u2019re things that some people do that invite demons into their lives without them knowing. They yield themselves to the influence of demons through certain kinds of music, dance, among several other things. For example, Christians who visit pornographic websites open themselves to demons. In the Old Testament, God forbade His children from looking at the nakedness of others (Read Leviticus 18).\xa0"}),"\n",(0,s.jsx)(n.p,{children:"Such things aren\u2019t acceptable to God. They\u2019re not consistent with the life of righteousness. Think about it: How does Christ come into the heart of a man? When you believe in Jesus Christ and confess His lordship, He comes into your heart in an instant. That\u2019s the same way a man can open his heart to demons when he exposes himself to things that are inspired by demons.\xa0"}),"\n",(0,s.jsx)(n.p,{children:"Protect and keep yourself from all unrighteousness. The Bible says*, \u201cFor the wrath of God is revealed from heaven against all ungodliness and unrighteousness of men, who hold the truth in unrighteousness\u201d* (Romans 1:18). Refuse to harbour unclean thoughts. Use the Word to purify your heart of anger, bitterness and malice."}),"\n",(0,s.jsxs)(n.p,{children:["The Word of God is a cleanser. As you meditate on the Word, you\u2019re cleansed from ALL unrighteousness: ",(0,s.jsx)(n.em,{children:"\u201cNow ye are clean through the word which I have spoken unto you\u201d"})," (John 15:3). Live as a Christian. Our theme verse says to keep your heart on the right course, on God\u2019s truth. Protect your mind and spirit with the Word."]}),"\n",(0,s.jsxs)(n.p,{children:["In Proverbs 23:26 (NIV), God says, ",(0,s.jsx)(n.em,{children:"\u201cMy son, give me your heart and let your eyes delight in my ways.\u201d"})," Let your heart delight in His Word, in His righteousness, and live a life worthy of Him\u2014pleasing Him in every way. Hallelujah!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"Satan, the god of this world, has nothing in me! The Lord has given me dominion over the flesh, to guard my heart and mind with all diligence. No unwholesome thought can take root in my heart. My spirit, my mind and my body are yielded to the Word and purified by the Word every day, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 John 2:16; Psalm 119:11; Proverbs 4:23"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Refuse to harbour unclean thoughts. Use the Word to purify your heart of anger, bitterness and malice. The Word of God is a cleanser. As you meditate on the Word, you\u2019re cleansed from ALL unrighteousness."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 8:48-59; 2 Kings 1-3",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 10:22-33; Proverbs 15"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"we-live-in-his-presence-may-20th-2022",children:"WE LIVE IN HIS PRESENCE May 20th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-20-we-live-in-his-presence.jpg",alt:"May 20th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Even the Spirit of truth; whom the world cannot receive, because it seeth him not, neither knoweth him: but ye know him; for he dwelleth with you, and shall be in you (John 14:17)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["In Christianity, we don\u2019t go in and out of God\u2019s presence. His presence is our home; we were born therein. When you study Psalm 87:4, you\u2019d observe the prophetic reference: ",(0,s.jsx)(n.em,{children:"\u201cI will make mention of Rahab and Babylon to them that know me: behold Philistia, and Tyre, with Ethiopia; this man was born there.\u201d"})," \xa0\u201cThis man\u201d refers to the new creation. \u201cThere\u201d refers to our place in Christ\u2014our place in God\u2019s presence."]}),"\n",(0,s.jsx)(n.p,{children:"This is the reason it\u2019s an absurdity when a Christian says, \u201cFather, I come to you today\u201d; or \u201cLet\u2019s get into the presence of God through worship.\u201d I often wonder: if they\u2019re about to get into the presence of God, where then have they been? God\u2019s children must understand that we live in His presence."}),"\n",(0,s.jsx)(n.p,{children:"There\u2019s no such thing in the New Testament as us \u201cgetting into\u201d the presence of God. In Him we live and move and have our being. His presence is in us and with us. When you come into a place, you come with His presence. If you\u2019re born again, nobody can take you into the presence of God, because you were born right into His presence, and you live in His presence, both legally and vitally. Hallelujah!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"I\u2019m the temple of the living God; He dwells in me by the Holy Spirit. Christ has taken up His abode in me and I\u2019m forever in Him. I live in His presence and I carry that divine presence everywhere to impact my world, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Acts 17:28; 1 Corinthians 3:16; Colossians 1:26-27"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"In Christianity, we don\u2019t go in and out of God\u2019s presence. His presence is our home; we were born therein. His presence is in us and with us."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 9:1-41; 2 Kings 4-5",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 11:1-11; Proverbs 16"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"praying-for-our-evangelical-work-may-21st-2022",children:"PRAYING FOR OUR EVANGELICAL WORK May 21st, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-21-praying-for-our-evangelical-work.jpg",alt:"May 21st, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"In whom the god of this world hath blinded the minds of them which believe not, lest the light of the glorious gospel of Christ, who is the image of God, should shine unto them (2 Corinthians 4:4)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["The Lord Jesus said in Matthew 9:37, ",(0,s.jsx)(n.em,{children:"\u201c\u2026The harvest truly is plenteous, but the labourers are few.\u201d"})," The harvest He\u2019s referring to is the souls of men, the world of sinners. The labourers are those who are reaching out with the Gospel to win these lost souls to Christ. That\u2019s why we have to pray and intercede for the harvest field, for the souls of men that we intend to reach within and beyond our catchments, because the harvest is ripe."]}),"\n",(0,s.jsx)(n.p,{children:"Prayer is our number one requirement in our evangelical work. As we intercede for them, we\u2019re to prophesy by the inspiration of the Holy Spirit. Our prophetic prayer is how we pour out righteousness and salvation on the nations. Set times for prayer, either as an individual, family, group or as a Church. Plan and map out definite prayer times for your evangelical work."}),"\n",(0,s.jsx)(n.p,{children:"If we would pray correctly and effectively as He has asked us to, our preaching will be effective. In some cases, you wouldn\u2019t even have to preach much or preach at all before sinners start crying out to the Lord for salvation."}),"\n",(0,s.jsxs)(n.p,{children:["Through prayer\u2014particularly praying in the Spirit\u2014 you pour forth gushers of living water (John 7:38). Your words become so effective just like Paul said in 1 Corinthians 2:4: ",(0,s.jsx)(n.em,{children:"\u201cAnd my speech and my preaching was not with enticing words of man\u2019s wisdom, but in demonstration of the Spirit and of power.\u201d"})," Then you find that through you, the heavens constantly rain down righteousness, and salvation is springing up everywhere. Hallelujah!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, your love, grace and goodness fill the nations, and the light of your glorious Gospel shines brightly, liberating men, women, boys and girls from darkness, bondage and corruption into the glorious liberty of the sons of God, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 Timothy 2:1-4; Isaiah 45:8 NASB"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Prayer is our number one requirement in our evangelical work. As we intercede for them, we\u2019re to prophesy by the inspiration of the Holy Spirit. Our prophetic prayer is how we pour out righteousness and salvation on the nations."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 10:1-21; 2 Kings 6-7",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 11:12-19; Proverbs 17"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"his-eternal-glory-in-us-may-22nd-2022",children:"HIS ETERNAL GLORY IN US May 22nd, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-22-his-eternal-glory-in-us.jpg",alt:"May 22nd, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"For God, who commanded the light to shine out of darkness, hath shined in our hearts, to give the light of the knowledge of the glory of God in the face of Jesus Christ (2 Corinthians 4:6)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"The opening verse is an extraordinary description of something scientifically impossible. Notice the verse isn\u2019t saying that God commanded the light to shine into the darkness; that would have been wonderful because the question then would be, \u201cWhere did the light come from?\u201d Then we\u2019d say, \u201cIt came from God; He shone into the darkness.\u201d"}),"\n",(0,s.jsx)(n.p,{children:"But this time, God didn\u2019t shine the light into the darkness; rather, He commanded the light to shine forth from it. So, the light came out of darkness! This is the same God that has shined in our hearts\u2014to give the light of the knowledge of the glory of God in the face of Jesus Christ."}),"\n",(0,s.jsxs)(n.p,{children:["Remember, there was the glory of God in the old tabernacle, in the days of Moses. That glory, the Bible says, faded away. But the one from Jesus Christ is eternal glory, excellent glory. That\u2019s the glory that resides in you today: ",(0,s.jsx)(n.em,{children:"\u201cBut we have this treasure in earthen vessels. That the excellency of the power may be of God, and not of us\u201d"})," (2 Corinthians 4:7)."]}),"\n",(0,s.jsx)(n.p,{children:"Every day, declare that you\u2019re a carrier of God\u2019s divine presence, a bearer of His glory. Your life isn\u2019t empty; there\u2019s a treasure of power, love, light and divine perfection in you. The excellence of His power permeates through you, causing everything you do to prosper. Glory to God!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"The glory of God that\u2019s within me beams forth from my spirit with overwhelming intensity. My life is the expression of God\u2019s grace, strength, perfection, beauty and righteousness. The excellence and virtues of divinity are manifested in and through me in ever-increasing splendour! I\u2019m continually transfigured from glory to glory, by the Spirit, as I study and meditate on the Word. Hallelujah!"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"2 Corinthians 3:7-11; 2 Corinthians 3:18 NIV"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Every day, declare that you\u2019re a carrier of God\u2019s divine presence, a bearer of His glory. Your life isn\u2019t empty; there\u2019s a treasure of power, love, light and divine perfection in you."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 10:22-42; 2 Kings 8-9",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 11:20-29; Proverbs 18"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"a-more-excellent-way-to-give-praise-may-23rd-2022",children:"A MORE EXCELLENT WAY TO GIVE PRAISE May 23rd, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-23-a-more-excellent-way-to-give-praise.jpg",alt:"May 23rd, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"And David danced before the LORD with all his might; and David was girded with a linen ephod (2 Samuel 6:14)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["There\u2019re those who thought that in the verse above David was merely dancing in the flesh. So, oftentimes they\u2019d say they want to dance like David danced, and they start gyrating with all their might. No! David was a man of the Spirit. When the Ark of God was being brought into the city of David, our theme verse says ",(0,s.jsxs)(n.em,{children:["\u201c...David danced before the LORD with all his might; ",(0,s.jsx)(n.strong,{children:"and David was girded with a linen ephod."}),"\u201d"]})]}),"\n",(0,s.jsx)(n.p,{children:"Notice the underlined part. In those days, dressing with a linen ephod represented the office of the priest. Whenever the priest was in that regalia, he functioned in his spiritual office. David was girded with a linen ephod; being also a prophet, evidently instructed by the Spirit, his dance as described in the Hebrew text was very different from the fleshly dance of normal celebrations."}),"\n",(0,s.jsx)(n.p,{children:"When the Bible says he danced before the Lord, the Hebrew word for dance is \u201dkarar\u201d, and it doesn\u2019t refer to the kind of whirling and gyrating that most people think. \u201cKarar\u201d means to skip and jump and spin, turning around about your own axis, like a child."}),"\n",(0,s.jsxs)(n.p,{children:["David\u2019s dance is similar to what Jesus did in Luke 10:21; the Bible says, ",(0,s.jsx)(n.em,{children:"\u201cIn that hour Jesus rejoiced (Greek: agalliao) in spirit\u2026.\u201d"})," He was skipping and jumping like a child. So, that David danced with all \u201chis might\u201d doesn\u2019t refer to exerting himself physically on the dance floor, like most people do today. The term is \u201coze, oze\u201d in Hebrew, meaning that he danced with all his glory, all his majesty; he danced with all of God\u2019s regalia on him. Glory to God!"]}),"\n",(0,s.jsx)(n.p,{children:"The best description for it in the New Testament is the Greek word \u201cagalliao\u201d; it\u2019s a dance from the inside. Dancing in the flesh distracts your spirit from true reverence and worship; it dissipates spiritual power, and many don\u2019t realize this."}),"\n",(0,s.jsxs)(n.p,{children:["In these last days, the Spirit of God is showing us a more excellent way to give God true praise and worship. The days of fleshly dance in Christian congregations are over. When we worship the Lord, it must and should always be in the spirit: ",(0,s.jsx)(n.em,{children:"\u201cFor we are the circumcision, which worship God in the spirit, and rejoice in Christ Jesus, and have no confidence in the flesh\u201d"})," (Philippians 3:3). Also, John 4:23-24 says, ",(0,s.jsx)(n.em,{children:"\u201cBut the hour cometh, and now is, when the true worshippers shall worship the Father in spirit and in truth: for the Father seeketh such to worship him. God is a Spirit: and they that worship him must worship him in spirit and in truth.\u201d"})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Lord, thank you for the privilege of worship and for guiding me on the way of true praise and worship that\u2019s acceptable to you. Thank you for granting me the ability to condition my mind and senses to offer you true worship from my heart that transmits your glory to impart my world, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"John 4:23-24 NIV; Philippians 3:3 NIV"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"In these last days, the Spirit of God is showing us a more excellent way to give God true praise and worship. The days of fleshly dance in Christian congregations are over."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 11:1-16; 2 Kings 10-12",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 11:30-34; Proverbs 19"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"called-to-lead-may-24th-2022",children:"CALLED TO LEAD May 24th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-24-called-to-lead.jpg",alt:"May 24th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Go ye therefore, and teach all nations, baptizing them in the name of the Father, and of the Son, and of the Holy Ghost: Teaching them to observe all things whatsoever I have commanded you: and, lo, I am with you alway, even unto the end of the world. Amen (Matthew 28:19-20)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["In our bid to evangelize the world, we must fully understand activism, advocacy and real leadership. Jesus said, ",(0,s.jsx)(n.em,{children:"\u201cGo ye therefore, and teach all nations\u2026.\u201d"})," It means we\u2019re to make pupils of the nations. The word \u201cnations\u201d is from the Greek \u201cEthnos.\u201d Ethnos refers to different groups with some common factors that bring them together. For example, a family, a group of politicians, an association of doctors, etc."]}),"\n",(0,s.jsxs)(n.p,{children:["So, when Jesus used the term \u201cnations\u201d you could interpret it as countries, families, groups, professional bodies, and so on. The Lord instructs that we go into all such places and lead. Remember, He said in Matthew 5:14, ",(0,s.jsx)(n.em,{children:"\u201cYe are the light of the world\u2026.\u201d"})," You\u2019re a leader in your world. Take up your role as a protagonist."]}),"\n",(0,s.jsx)(n.p,{children:"A protagonist is an advocate or champion of a particular cause or idea. One of the ways a protagonist works is through innovation. You open your mind and find new ways of getting in and getting ahead with the Gospel. As a protagonist, develop more cutting-edge means and strategies for the advancement of the Kingdom."}),"\n",(0,s.jsx)(n.p,{children:"Always think about how you can reach more people or a whole \u201cnation\u201d for the Lord. Then, come up, by the Spirit, with innovative ideas to accomplish it. Remember, you\u2019re the Lord\u2019s voice where you are. He sent you into that world so you could show others how to live the Kingdom-life. You\u2019re there to make a difference! Create new opportunities for the spread of the Gospel."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, my mind is replete with supernatural ideas and strategies to push the Gospel to the ends of the earth. I pray for open and effectual doors for expansion and increased influence of the Gospel in different \u201cethnos\u201d; I declare your righteousness will continue to dominate and prevail in the earth until the coming of the Lord, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Matthew 5:14-16; Romans 1:16; Mark 6:15"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"You\u2019re the Lord\u2019s voice where you are. He sent you into that world so you could show others how to live the Kingdom-life. You\u2019re there to make a difference! Create new opportunities for the spread of the Gospel."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 11:17-57; 2 Kings 13-15",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 12:1-11; Proverbs 20"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"love-like-christ-may-25th-2022",children:"LOVE LIKE CHRIST May 25th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-25-love-like-christ.jpg",alt:"May 25th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Above all, love each other deeply, because love covers over a multitude of sins (1 Peter 4:8 NIV)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["If someone does something that upsets or offends you, rather than become bitter or enraged, let it go. Jesus showed us a good example of how to walk in love. He loved everyone, including His accusers and persecutors. Then He tells us in Matthew 5:44, ",(0,s.jsx)(n.em,{children:"\u201cBut I say unto you, Love your enemies, bless them that curse you, do good to them that hate you, and pray for them which despitefully use you, and persecute you.\u201d"}),"\xa0"]}),"\n",(0,s.jsx)(n.p,{children:"Emulate the Master. Have the love language and culture. Love everyone unconditionally. The Bible says*, \u201cFor even hereunto were ye called: because Christ also suffered for us, leaving us an example, that ye should follow his steps: Who did no sin, neither was guile found in his mouth: Who, when he was reviled, reviled not again; when he suffered, he threatened not; but committed himself to him that judgeth righteously\u201d* (1 Peter 2:21-23)."}),"\n",(0,s.jsxs)(n.p,{children:["Someone once asked, \u201cIn the case where the people you respect are those that hurt you, what should you do?\u201d Honour them nonetheless. Love them. Grow out of that place where you can still be hurt. Live in God\u2019s Word. Live like Christ and love like Him: ",(0,s.jsx)(n.em,{children:"\u201cBe ye therefore followers of God, as dear children; And walk in love, as Christ also hath loved us, and hath given himself for us an offering and a sacrifice to God for a sweetsmelling savour\u201d"})," (Ephesians 5:1-2).\xa0"]}),"\n",(0,s.jsx)(n.p,{children:"1 Corinthians 13:4-8 AMPC says*, \u201cLove endures long and is patient and kind; love never is envious nor boils over with jealousy, is not boastful or vainglorious, does not display itself haughtily. It is not conceited (arrogant and inflated with pride); it is not rude (unmannerly) and does not act unbecomingly. Love (God\u2019s love in us) does not insist on its own rights or its own way, for it is not self-seeking; it is not touchy or fretful or resentful; it takes no account of the evil done to it [it pays no attention to a suffered wrong]. It does not rejoice at injustice and unrighteousness, but rejoices when right and truth prevail. Love bears up under anything and everything that comes, is ever ready to believe the best of every person, its hopes are fadeless under all circumstances, and it endures everything [without weakening]. Love never fails [never fades out or becomes obsolete or comes to an end] \u2026.\u201d*"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for the love of Christ which is the bond of perfection and which rules my heart always. I lavishly express your love-nature and radiate the beauty and glories of heaven to my world. Always, I rise above offences, being accommodating and kind; I cover others with the umbrella of your love that flows lavishly from the castle of my heart, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Colossians 3:12-14 GNB; John 13:34-35; 1 Corinthians 13:4-7 NIV"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"If someone does something that upsets or offends you, rather than become bitter or enraged, let it go. Jesus showed us a good example of how to walk in love. He loved everyone, including His accusers and persecutors."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 12:1-19; 2 Kings 16-17",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 12:12-21; Proverbs 21"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"we-only-get-stronger-may-26th-2022",children:"WE ONLY GET STRONGER May 26th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-26-we-only-get-stronger.jpg",alt:"May 26th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"And Moses was an hundred and twenty years old when he died: his eye was not dim, nor his natural force abated (Deuteronomy 34:7)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Have you ever heard the word \u201cagelessness\u201d? It\u2019s part of our heritage in Christ. We grow, but we don\u2019t age! Hallelujah! So never say, \u201cYou know, I\u2019m no longer as strong as I used to be; age isn\u2019t on my side.\u201d As a Christian, you shouldn\u2019t talk like that because when you were born again, eternal life was imparted to your spirit. Eternal life is life with agelessness."}),"\n",(0,s.jsx)(n.p,{children:"In Christ, you\u2019ve been brought into His glory and the glorified life. So, every so often, declare to yourself, \u201cI only get stronger! I\u2019m stronger today than I was ten years ago!\u201d Consider Moses: at one hundred and twenty years of age, his physical stature wasn\u2019t abated, his eyes weren\u2019t dim. Yet, the Bible says the glory that Moses had isn\u2019t anything compared to what we have today in Christ Jesus (2 Corinthians 3:7-11)."}),"\n",(0,s.jsxs)(n.p,{children:["It was the same with Caleb: At the age of eighty-five, Caleb said, ",(0,s.jsx)(n.em,{children:"\u201cI\u2019m as strong today as I was when I was forty; therefore, give me this mountain\u201d"})," (Read Joshua 14:7-12). That\u2019s our story! Our youth is renewed like the eagle\u2019s (Psalm 103:5). For us, there\u2019s only one way: We get stronger and stronger, greater and greater, we move upward and forward. Hallelujah!"]}),"\n",(0,s.jsxs)(n.p,{children:["Never see yourself as weak. The Bible says, ",(0,s.jsx)(n.em,{children:"\u201cFor which cause we faint not; but though our outward man perish, yet the inward man is renewed day by day\u201d"})," (2 Corinthians 4:16). We\u2019re continually strengthened, invigorated from within by the Holy Ghost. Take these thoughts that I share with you seriously and live the supernatural life in Christ. Praise God!"]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"The Lord is the strength of my life and He constantly renews, refreshes and rejuvenates me through the Word and the power of the Holy Spirit! I\u2019m stronger by the day, stronger today than ever, because my inward man is renewed day by day; I live in the ageless zone. Glory to God!"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Joshua 14:7-12; Psalm 92:13-14"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Have you ever heard the word \u201cagelessness\u201d? It\u2019s part of our heritage in Christ. We grow, but we don\u2019t age! When you were born again, eternal life was imparted to your spirit. Eternal life is life with agelessness."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 12:20-50; 2 Kings 18-19",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 12:22-31; Proverbs 22"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"the-walk-of-a-christian-may-27th-2022",children:"THE WALK OF A CHRISTIAN May 27th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-27-the-walk-of-a-christian.jpg",alt:"May 27th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"I therefore, the prisoner of the Lord, beseech you that ye walk worthy of the vocation wherewith ye are called (Ephesians 4:1)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["One of the dynamics of Christianity is derived from a very important word from the verse above: WALK! There\u2019s the walk of a Christian. When you\u2019re born again, the first thing God wants to help you learn is how to walk in Christ in your daily life. Colossians 2:6 says, ",(0,s.jsx)(n.em,{children:"\u201cAs ye have therefore received Christ Jesus the Lord, so walk ye in him\u201d"}),"\xa0(Colossians 2:6)."]}),"\n",(0,s.jsxs)(n.p,{children:["How you live every day, how you relate with other people, how you relate with God\u2014and the kind of mind you have\u2014these are very important to God. There\u2019s a way a Christian should and must live that distinguishes him or her from a non\u2013Christian. Someone may be born again and not live the Christian life. In fact, some don\u2019t even know what a Christian life really is. But the Bible says, ",(0,s.jsx)(n.em,{children:"\u201cThis I say therefore, and testify in the Lord, that ye henceforth walk not as other Gentiles walk, in the vanity of their mind\u201d"})," (Ephesians 4:17)."]}),"\n",(0,s.jsx)(n.p,{children:"This is one of the reasons churches and pastors are important, because the spiritual life requires training. God brings His children into congregations, church assemblies, and gives them pastors after His heart to train them, teach them, and build them through the Word (Read Ephesians 4:11-14)."}),"\n",(0,s.jsxs)(n.p,{children:["God expects that in the Church, every new person that receives Christ, that becomes a member of the church, is trained in the Christian walk. Then, He tells us why: ",(0,s.jsx)(n.em,{children:"\u201cThat we henceforth be no more children, tossed to and fro, and carried about with every wind of doctrine, by the sleight of men, and cunning craftiness, whereby they lie in wait to deceive\u201d"})," (Ephesians 4:14)."]}),"\n",(0,s.jsxs)(n.p,{children:["In the 15th verse, He tells us the end-result\u2014growth: ",(0,s.jsx)(n.em,{children:"\u201cBut speaking the truth in love, may grow up into him in all things, which is the head, even Christ.\u201d"})," The Christian walk isn\u2019t just for baby Christians. You begin learning it as a new convert, but God expects you to grow unto maturity and become firmly rooted in the faith, and live triumphantly every day to the praise and glory of His Name."]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Confession"}),(0,s.jsx)(n.p,{children:"Through the Gospel, I\u2019ve been separated from the world unto God, from unrighteousness unto righteousness, and from darkness to light. I open my heart and mind to be trained and educated in the things of God. And through the Word, and by the Spirit, I\u2019m growing unto maturity, firmly rooted in the faith to live triumphantly always. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"1 Timothy 3:15; 1 Peter 2:2; Ephesians 4:1 AMPC"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"There\u2019s the walk of a Christian. When you\u2019re born again, the first thing God wants to help you learn is how to walk in Christ in your daily life."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 13:1-30; 2 Kings 20-22",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 13:1-13; Proverbs 23"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"music-is-beyond-lyrics-may-28th-2022",children:"MUSIC IS BEYOND LYRICS May 28th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-28-music-is-beyond-lyrics.jpg",alt:"May 28th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"And God spake unto Moses, and said unto him, I am the LORD: And I appeared unto Abraham, unto Isaac, and unto Jacob, by the name of God Almighty, but by my name JEHOVAH was I not known to them (Exodus 6:2-3)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"There\u2019re some things that have crept into the house of God for many years, ruining people\u2019s lives and many never knew. For example, some of the different styles of music and dance in some churches today, where did they come from? What spirits are behind such music and dance? The spirit behind certain music or dance is the significance of it."}),"\n",(0,s.jsx)(n.p,{children:"If, for instance, a Christian likes to sing or dance to Reggae music, as much as we may appreciate Reggae music, the question would be, \u201cWhat\u2019s the source of Reggae music?\u201d Music isn\u2019t just the lyrics; make no mistakes about it. The sound of music is important just as the source is important."}),"\n",(0,s.jsx)(n.p,{children:"Certain music could attract demons into people\u2019s lives without them knowing. There\u2019re things that God allows us to do for a certain time of our lives, and after a while He\u2019ll demand a change, knowing that it was within His permissive will. When He brings in that change, we must be willing to embrace it."}),"\n",(0,s.jsx)(n.p,{children:"We read in our theme scripture what He said to Moses. He said to him, \u201cAbraham, Isaac and Jacob knew me as El Shaddai; they didn\u2019t know me as Jehovah; but I am Jehovah.\u201d Why was this important? By the revelation of EL Shaddai, Moses wouldn\u2019t be able to bring the children of Israel out of Egypt. Moses would need the \u201cJehovah\u201d revelation, and by the hand of Jehovah, he\u2019d deliver the children of Israel out of Egypt. That\u2019s the purpose of the revelation that was given to Moses."}),"\n",(0,s.jsx)(n.p,{children:"Likewise, as we prepare for the coming of the Lord, there\u2019re certain things we must change based on the guidance of the Spirit and revelation from His Word. There\u2019s a way He wants us to live, serve and worship Him, especially through music. We must be open and willing to receive His guidance and correction. Praise God!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, thank you for unveiling to me these treasures of wisdom and training, and for educating my spirit on spiritual realities, to walk in righteousness in my worship of you. My sacrifice of praise and worship come to you as sweet-smelling savour, because they\u2019re offered from a heart washed from all worldliness and impurities, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Acts 17:30 TPT; 1 John 2:15; 2 Corinthians 6:17 GNB"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Music isn\u2019t just the lyrics; make no mistakes about it. The sound of music is important just as the source is important. Certain music could attract demons into people\u2019s lives without them knowing."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 13:31-38; John 14:1-14; 2 Kings 23-25",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 14:1-9; Proverbs 24"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"the-church-is-gods-security-system-may-29th-2022",children:"THE CHURCH IS GOD\u2019S SECURITY SYSTEM May 29th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-29-the-church-is-gods-security-system.jpg",alt:"May 29th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"Now the Spirit speaketh expressly, that in the latter times some shall depart from the faith, giving heed to seducing spirits, and doctrines of devils (1 Timothy 4:1)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"In some parts of the world today, there\u2019s a satanic plan to go after the children. Some are peddling the idea that children don\u2019t have to listen to their parents, because they (the children) belong to the state. But beyond the children, the strategy is actually an outward expression of a spiritual attack that\u2019s also taking place. There\u2019s a satanic move to give people an impression that churches aren\u2019t important. So, they say, \u201cYou don\u2019t have to be a member of a church; just serve God generally and you\u2019ll be fine.\u201d They spread the idea that you can just be raised through the internet, listening to different preachers online."}),"\n",(0,s.jsx)(n.p,{children:"What they\u2019re trying to do is an attempt to pull those Christians out of the security system that God created for families, thereby exposing them to satanic attacks. Therefore, be smart. The Bible says an heir, as long as he\u2019s a child, he\u2019s placed under tutors and governors until a time set by the Father (Galatians 4:1-2). Also, he tells us to not forsake the assembly of the local church as some do in bad behaviour (Hebrews 10:25)."}),"\n",(0,s.jsx)(n.p,{children:"When someone says, \u201cI go to many churches, I don\u2019t have any particular one,\u201d it\u2019s proof that he or she has been deceived by the modus operandi of Satan. The structures that God has set in His Word are important; they have to be used for the purposes for which the Lord set them. You not only grow in the church, you also learn and participate in ministry to others."}),"\n",(0,s.jsx)(n.p,{children:"In the church, the pastoral ministry nurtures the brethren, trains them in the prayer life, faith, and to live the Christian life victoriously daily as we await the imminent return of Christ. So, when you win souls, salvation is only the beginning; guide them to church. It\u2019s for their security, spiritual growth, edification and ministry."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Lord, thank you for the opportunity to be a member of the body of Christ and participate actively in a local assembly. I pray for new converts around the world alongside those who may have been misled; that they\u2019d understand the Church as your security system for them, a place of refuge, comfort, and refreshing. And as they come in, they\u2019ll grow spiritually and be effective in the work and ministry of reconciliation, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Hebrews 10:25; Luke 4:16; 1 Timothy 3:15"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"When you win souls, salvation is only the beginning; guide them to church. It\u2019s for their security, spiritual growth, edification and ministry. In the church, the pastoral ministry nurtures them to live the Christian life victoriously daily."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 14:15-31; 1 Chronicles 1-2",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 14:10-19; Proverbs 25"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"grace-heaped-upon-grace-may-30th-2022",children:"GRACE HEAPED UPON GRACE May 30th, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-30-grace-heaped-upon-grace.jpg",alt:"May 30th, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"But grow in grace, and in the knowledge of our Lord and Saviour Jesus Christ. To him be glory both now and forever Amen (2 Peter 3:18)."})})}),"\n"]}),"\n",(0,s.jsxs)(n.p,{children:["Did you know that you could walk in ever-increasing grace? This is what God wants for your life: grace heaped upon grace such that no matter the challenges you face, the grace to put you over is available. John 1:16 says, ",(0,s.jsx)(n.em,{children:"\u201cAnd of his fullness have all we received, and grace for grace.\u201d"})," The Amplified version says, ",(0,s.jsx)(n.em,{children:"\u201cFor out of His fullness [the superabundance of His grace and truth] we have all received grace upon grace [spiritual blessing upon spiritual blessing, favor upon favor, and gift heaped upon gift].\u201d"})]}),"\n",(0,s.jsx)(n.p,{children:"The Greek margin actually says, \u201cGrace heaped upon grace.\u201d This encompasses spiritual blessings, favours, gifts, etc. Every day, be conscious that you have grace for whatever you need and for anything you have to do. The grace of God on your life is the inner working of the glory and power of God that radiates excellence. That grace causes you to have uncommon results."}),"\n",(0,s.jsx)(n.p,{children:"Your contemporaries might be as gifted as you or even have more abilities than yourself, but God\u2019s grace at work in you will take you to a higher plane and cause you to have results that completely outclass theirs. Hallelujah."}),"\n",(0,s.jsx)(n.p,{children:"Promotion in your life means more grace. If you\u2019re promoted with more responsibilities without the accompanying grace, you may not succeed at that new level. We understand how this works from a simple verse in the Bible: James 4:6; it says*, \u201cBut he giveth more grace\u2026.\u201d*"}),"\n",(0,s.jsx)(n.p,{children:"Notice it doesn\u2019t say, \u201cHe gave more grace\u201d; rather, it says, \u201cHe GIVES more grace.\u201d That means you can enter into that verse every day, every week, every year; it\u2019s a word that\u2019s active and can be activated anytime. That\u2019s how the prophetic word of God works; claim this for your life today."}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, I receive more grace and increase in the workings of your power in my life. The knowledge of your Word causes me to walk in an overflow of grace, and that grace produces favour, beauty, goodwill, loveliness, perfections, glory and ability in my life, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"2 Peter 1:2; 2 Corinthians 9:8; James 4:6"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Every day, be conscious that you have grace for whatever you need and for anything you have to do. The grace of God on your life is the inner working of the glory and power of God."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 15:1-17; 1 Chronicles 3-4",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 14:20-30; Proverbs 26"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.h2,{id:"be-visionary-with-the-gospel-may-31st-2022",children:"BE VISIONARY WITH THE GOSPEL May 31st, 2022"}),"\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.img,{src:"https://rhapsodyofrealities.b-cdn.net/app/dailyarticle/may-22-day-31-be-visionary-with-the-gospel.jpg",alt:"May 31st, 2022"})}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(n.blockquote,{children:["\n",(0,s.jsx)(n.p,{children:(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"In such ways I have trailblazed a preaching of the Message of Jesus all the way from Jerusalem far into northwestern Greece. This has all been pioneer work, bringing the Message only into those places where Jesus was not yet known and worshiped (Romans 15:19-20 MSG)."})})}),"\n"]}),"\n",(0,s.jsx)(n.p,{children:"Paul was a trailblazer, a visionary apostle. What we read in our opening verse reveals his mindset and passion to preach and share the Gospel in places where Christ hadn\u2019t been known. He was in the vanguard of taking the glorious Gospel to new, unconverted souls."}),"\n",(0,s.jsx)(n.p,{children:"Wherever Paul went, he began to research if Christ\u2019s name had already been known in such a place. And when he found that the Gospel had been established in that city, he simply moved into new cities and regions where the message wasn\u2019t known, and pioneered the work. This was the kind of ministry that Paul had: ever-increasing vision!"}),"\n",(0,s.jsxs)(n.p,{children:["It wasn\u2019t that Paul was ignoring other places and looking only for places where Christ hadn\u2019t been preached; no! Read the statement he made before what we read in our opening scripture; he said, ",(0,s.jsx)(n.em,{children:(0,s.jsx)(n.strong,{children:"\u201c...from Jerusalem, and round about unto Illyricum, I have fully preached the gospel of Christ\u201d"})})," (Romans 15:19). Think about that!"]}),"\n",(0,s.jsx)(n.p,{children:"Emulate Paul\u2019s kind of vision, where you take the map of your city and research to know where the light of the Gospel is still dim or non-existent, with the goal of moving in with the Gospel. If there\u2019re places you can\u2019t go physically, you send in Bibles and Rhapsody of Realities."}),"\n",(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.em,{children:"\u201c...This Gospel of the Kingdom shall be preached in all the world for a witness unto all nations, and then shall the end come\u201d"})," (Matthew 24:14). Those are the words of Jesus; they must be personal to you. Be passionate about spreading the message of the Gospel, not just in your catchment but also to the regions beyond. Pioneer new cells, fellowships and churches in places where you\u2019re aware they don\u2019t exist. The Lord\u2019s mandate is still in force. He said, ",(0,s.jsx)(n.em,{children:"\u201c\u2026Go ye into all the world, and preach the gospel to every creature\u201d"})," (Mark 16:15).\xa0"]}),"\n",(0,s.jsx)(n.p,{children:"Utilize every opportunity to preach the Gospel. The harvest is ready. Hallelujah!"}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Prayer"}),(0,s.jsx)(n.p,{children:"Dear Father, I thank you for committing to my trust the ministry of the Gospel. Through me, the light of the Gospel shines brightly in cities, regions and nations, resulting in salvation and impartation of eternal life to many who have never known you, in Jesus\u2019 Name. Amen."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Further Study"}),(0,s.jsx)(n.p,{children:"Matthew 5:14;1 Corinthians 9:16; Romans 15:19-20 AMPC"})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Excerpt"}),(0,s.jsx)(n.p,{children:"Emulate Paul\u2019s kind of vision, where you take the map of your city and research to know where the light of the Gospel is still dim or non-existent, with the goal of moving in with the Gospel."})]}),"\n",(0,s.jsxs)(t,{children:[(0,s.jsx)("summary",{children:"Bible Reading Plan"}),(0,s.jsxs)(n.p,{children:[(0,s.jsx)(n.strong,{children:"Annual"})," : John 15:18-27; John 16:1-16; 1 Chronicles 5-6",(0,s.jsx)(n.br,{}),"\n",(0,s.jsx)(n.strong,{children:"Bi-Annual"})," : 1 Corinthians 14:31-40; Proverbs 27"]})]}),"\n",(0,s.jsx)(n.hr,{}),"\n",(0,s.jsx)(n.hr,{})]})}function u(e={}){const{wrapper:n}={...(0,i.R)(),...e.components};return n?(0,s.jsx)(n,{...e,children:(0,s.jsx)(d,{...e})}):d(e)}},28453:(e,n,t)=>{t.d(n,{R:()=>o,x:()=>a});var s=t(96540);const i={},r=s.createContext(i);function o(e){const n=s.useContext(r);return s.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(i):e.components||i:o(e.components),s.createElement(r.Provider,{value:n},e.children)}}}]);